00362 bool DIOLINUXGPIORASPBERRYPI::RPI_Ini()
00363 {
00364   int fd;
00365 
00366   if((fd = open ("/dev/mem", O_RDWR | O_SYNC | O_CLOEXEC)) < 0) return false;
00367 
00368   RPI_gpio = (uint32_t *)mmap(0, RPI_BLOCK_SIZE, PROT_READ|PROT_WRITE, MAP_SHARED, fd, RPI_gpio_base) ;
00369   if (!RPI_CheckHandle(RPI_gpio)) return false;
00370 
00371   return true;
00372 }
