00505 bool DIOLINUXGPIORASPBERRYPI::RPI_GPIOWrite(XQWORD GPIO, bool isactive)
00506 {
00507   uint8_t gpiotoGPSET [] = {  7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7, 7,
00508                               8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8, 8,
00509                            };
00510 
00511 
00512   uint8_t gpiotoGPCLR [] = { 10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,10,
00513                              11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,11,
00514                            };
00515 
00516 
00517   if(!RPI_CheckHandle(RPI_gpio))  return false;
00518   if(!RPI_IsGPIOValid(GPIO))      return false;
00519 
00520   //XTRACE_PRINTCOLOR(1, __L("RPI Data Port Write: GPIO %lld ->%s"), GPIO, isactive?__L("on"):__L("off"));
00521 
00522   if(isactive)   *(RPI_gpio + gpiotoGPSET[GPIO]) = 1 << (GPIO & 31);
00523           else   *(RPI_gpio + gpiotoGPCLR[GPIO]) = 1 << (GPIO & 31);
00524 
00525   
00526   return true;
00527 }
