00127 bool DIOLINUXSTREAMI2C::Open()
00128 {
00129   if(!config) return false;
00130 
00131   if(!threadconnection)  return false;
00132   
00133   XBUFFER charstr;
00134   
00135   (*config->GetLocalDeviceName()).ConvertToASCII(charstr);
00136   handle = open(charstr.GetPtrChar(), O_RDWR);  
00137   if(handle<0) return false;
00138 
00139   switch(config->GetAddressSize())
00140     {
00141       case DIOSTREAMI2CCONFIG_ADDRESSSIZE_DEFAULT :
00142       case DIOSTREAMI2CCONFIG_ADDRESSSIZE_7BITS   : break;
00143       case DIOSTREAMI2CCONFIG_ADDRESSSIZE_10BITS  : if(ioctl(handle, DIOLINUXSTREAMI2C_TENBIT, 1) < 0)  return false;
00144                                                     break;
00145     }
00146 
00147   if(ioctl(handle, DIOLINUXSTREAMI2C_SLAVE , config->GetRemoteAddress()) < 0)                 return false;
00148 
00149   SetEvent(DIOSTREAMI2C_FSMEVENT_CONNECTED);
00150 
00151   status = DIOSTREAMSTATUS_CONNECTED;
00152 
00153   ResetXBuffers();
00154   ResetConnectionStatistics();
00155 
00156   return threadconnection->Ini();
