00646   int opt = 1;
00647   if(setsockopt(handlesocket, SOL_SOCKET, SO_KEEPALIVE , &opt, sizeof(opt)) < 0)
00648     {
00649       SetEvent(DIOLINUXTCPIPFSMEVENT_DISCONNECTING);
00650       SetLastDIOError(DIOSTREAMERROR_UNKNOWN);
00651       return false;
00652     }
00653 
00654   int flags = fcntl(handlesocket, F_GETFL, 0);
00655   fcntl(handlesocket, F_SETFL, flags | O_NONBLOCK);
