00658     {
00659       if(errno == EADDRINUSE)
00660         {
00661           SetEvent(DIOLINUXTCPIPFSMEVENT_DISCONNECTING);
00662           SetLastDIOError(DIOSTREAMERROR_ADDRINUSE);
00663           return false;
