00783 
00784                                                                 int esize = diostream->outbuffer->GetSize();
00785                                                                 if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00786 
00787                                                                 if(esize)
00788                                                                   {
00789                                                                     diostream->outbuffer->SetBlocked(true);
00790                                                                     XDWORD size = send(diostream->handlesocket,(char*)diostream->outbuffer->Get(), esize,0);
00791                                                                     diostream->outbuffer->SetBlocked(false);
00792 
00793                                                                     if(size == -1)
00794                                                                       {
00795                                                                         diostream->SetEvent(DIOLINUXTCPIPFSMEVENT_DISCONNECTING);
00796                                                                         break;
00797                                                                       }
00798 
00799                                                                     if(size) diostream->outbuffer->Extract(NULL, 0, size);
00800                                                                   }
00801                                                               }
00802                                                           }
00803                                                           break;
00804 
00805           case DIOLINUXTCPIPFSMSTATE_SENDINGDATA        : break;
00806 
00807           case DIOLINUXTCPIPFSMSTATE_DISCONNECTING      : break;
00808 
00809         }
00810     }
00811    else
00812     {
00813       if(diostream->GetEvent()<DIOLINUXTCPIP_LASTEVENT)
00814         {
00815           diostream->CheckTransition();
00816 
00817           switch(diostream->GetCurrentState())
00818             {
00819               case DIOLINUXTCPIPFSMSTATE_NONE               : break;
00820 
00821               case DIOLINUXTCPIPFSMSTATE_GETTINGCONNECTION   : if(diostream->enumservers)
00822                                                                 {
00823                                                                   if(diostream->enumservers->GetXTimer()) diostream->enumservers->GetXTimer()->AddSeconds(DIOSTREAMTCPIP_TIMEOUTENUMSERVERSIGNAL);
00824                                                                 }
00825 
00826                                                               if(diostream->config->IsServer())
00827                                                                 {
00828                                                                   diostream->config->SetCounterMultiServer(diostream->config->GetCounterMultiServer()+1);
00829                                                                 }
00830                                                               break;
00831 
00832               case DIOLINUXTCPIPFSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_CONNECTED);
00833                                                                 xevent.SetDIOStream(diostream);
00834                                                                 diostream->PostEvent(&xevent);
00835 
00836                                                                 diostream->SetEvent(DIOLINUXTCPIPFSMEVENT_WAITINGTOREAD);
00837                                                               }
00838                                                               break;
00839 
00840               case DIOLINUXTCPIPFSMSTATE_WAITINGTOREAD      : break;
00841 
00842               case DIOLINUXTCPIPFSMSTATE_SENDINGDATA        : break;
00843 
00844               case DIOLINUXTCPIPFSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00845                                                                 xevent.SetDIOStream(diostream);
00846 
00847                                                                 diostream->PostEvent(&xevent);
00848 
00849                                                                 diostream->threadconnection->Run(false);
00850                                                                 diostream->status       = DIOSTREAMSTATUS_DISCONNECTED;
00851                                                               }
00852                                                               break;
00853 
00854             }
00855         }
00856     }
00857 }
00858 
00859 
00870 void DIOLINUXSTREAMTCPIP::Clean()
00871 {
00872   threadconnection   = NULL;
00873   handlesocket      = -1;
00874 }
