00170 bool DIOLINUXSTREAMUART::Open()
00171 {
00172   if(!config) return false;
00173 
00174   XSTRING ttyS;
00175 
00176   if(!config->GetLocalDeviceName()->IsEmpty())
00177     {
00178       ttyS = config->GetLocalDeviceName()->Get();
00179     }
00180    else
00181     {
00182       ttyS.Format(__L("/dev/ttyS%d"),config->GetPort());
00183     }
00184 
00185   XBUFFER charstr;
00186   
00187   ttyS.ConvertToASCII(charstr);  
