00189   if(fd<0) return false;
00190 
00191   SetRTS(false);
00192   SetDTR(false);
00193 
00194   CleanBuffers();
00195 
00196   #ifdef LINUX
00197   fcntl(fd, F_SETFL, FNDELAY);
00198   #else
00199   fcntl(fd, F_SETFL, O_NONBLOCK);
00200   #endif
00201 
00202   Config();
00203 
00204   SetEvent(DIOLINUXUARTFSMEVENT_CONNECTED);
00205 
00206   status = DIOSTREAMSTATUS_CONNECTED;
00207 
