00559 bool DIOLINUXSTREAMUART::SetRTS(bool on)
00560 {
00561   if(fd<0) return false;
00562 
00563   int _status;
00564 
00565   ioctl(fd, TIOCMGET, &_status); // get the serial port status
00566 
00567   if(on) _status &= ~TIOCM_RTS; else _status |= TIOCM_RTS;
00568 
00569   ioctl(fd, TIOCMSET, &_status); // set the serial port status
00570 
00571   return true;
00572 }
