00633 void DIOLINUXSTREAMUART::ThreadConnection(void* param)
00634 {
00635   DIOLINUXSTREAMUART* diostream = (DIOLINUXSTREAMUART*)param;
00636   if(!diostream) return;
00637 
00638   if(diostream->GetEvent()==DIOLINUXUARTFSMEVENT_NONE) // No hay nuevos Eventos
00639     {
00640       switch(diostream->GetCurrentState())
00641         {
00642           case DIOLINUXUARTFSMSTATE_NONE                    : break;
00643 
00644           case DIOLINUXUARTFSMSTATE_CONNECTED               : break;
00645 
00646           case DIOLINUXUARTFSMSTATE_WAITINGTOREAD           : {
00647 
00648                                                                 if(!diostream->IsBlockRead())
00649                                                                   {
00650                                                                     XBYTE buffer[DIOSTREAM_MAXBUFFER];
00651 
00652                                                                     XDWORD br = diostream->ReadDirect(buffer, DIOSTREAM_MAXBUFFER);
00653                                                                     if(br)
00654                                                                       {
00655                                                                         //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(2), (XBYTE*)diostream->buffer, (int)br);
00656                                                                         diostream->inbuffer->Add(buffer, br);
00657                                                                       }
00658                                                                   }
00659 
00660                                                                 if(!diostream->IsBlockWrite())
00661                                                                   {
00662                                                                     int esize = diostream->outbuffer->GetSize();
00663                                                                     if(esize)
00664                                                                       {
00665                                                                         if(esize>DIOSTREAM_MAXBUFFER) esize = DIOSTREAM_MAXBUFFER;
00666 
00667                                                                         diostream->outbuffer->SetBlocked(true);
00668                                                                         XDWORD bw = diostream->WriteDirect(diostream->outbuffer->Get(), esize);
00669                                                                         diostream->outbuffer->SetBlocked(false);
00670 
00671                                                                         if(bw)
00672                                                                           {
00673                                                                             //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(4), (XBYTE*)diostream->buffer, (int)bw);
00674                                                                             diostream->outbuffer->Extract(NULL, 0, bw);
00675                                                                           }
00676                                                                       }
00677                                                                   }
00678                                                               }
00679                                                               break;
00680 
00681           case DIOLINUXUARTFSMSTATE_DISCONNECTING           : break;
00682 
00683         }
00684     }
00685    else
00686     {
00687       if(diostream->GetEvent()<DIOLINUXUART_LASTEVENT)
00688         {
00689           diostream->CheckTransition();
00690 
00691           switch(diostream->GetCurrentState())
00692             {
00693               case DIOLINUXUARTFSMSTATE_NONE                : break;
00694 
00695               case DIOLINUXUARTFSMSTATE_CONNECTED           : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00696                                                                 xevent.SetDIOStream(diostream);
00697                                                                 diostream->PostEvent(&xevent);
00698 
00699                                                                 diostream->SetEvent(DIOLINUXUARTFSMEVENT_WAITINGTOREAD);
00700                                                               }
00701                                                               break;
00702 
00703               case DIOLINUXUARTFSMSTATE_WAITINGTOREAD       : break;
00704 
00705               case DIOLINUXUARTFSMSTATE_DISCONNECTING       : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00706                                                                 xevent.SetDIOStream(diostream);
00707                                                                 diostream->PostEvent(&xevent);
00708 
00709                                                                 diostream->threadconnection->Run(false);
00710                                                                 diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00711                                                               }
00712                                                               break;
00713             }
00714         }
00715     }
00716 }
