00369 XDWORD DIOLINUXSTREAMUSB::ReadBuffer(XBYTE* buffer,XDWORD size)
00370 {
00371   if(GetConnectStatus()==DIOSTREAMSTATUS_DISCONNECTED) return 0;
00372 
00373   fd_set         fds;
00374   struct timeval tv;
00375 
00376   tv.tv_sec  = 0;
00377   tv.tv_usec = readtimeout;
00378 
00379   FD_ZERO(&fds);
00380   FD_SET(fd, &fds);
00381 
00382   int br = 0;
00383 
00384   if(select(fd+1, &fds, NULL, NULL, &tv) > 0)
00385     {
00386       br = read(fd, buffer, size);
00387 
00388       if(br< 0)
00389         {
00390           return 0;
00391         }
00392     }
00393 
00394   return (XDWORD)br;
