00560 bool DIOLINUXWIFIMANAGERMODE::WPA_GetListRemoteAP(XVECTOR<XSTRING*>& nameAPs)
00561 { 
00562   XSTRING   prefixcommand;
00563   XSTRING   command;
00564   XSTRING   output;
00565   bool      status;
00566 
00567 
00568   nameAPs.DeleteContents();
00569   nameAPs.DeleteAll();
00570 
00571   prefixcommand.Format(DIOLINUXWIFIMANAGERMODE_PREFIXCOMMAND, netinterface.Get());
00572 
00573   command.Format(__L("%s scan") , prefixcommand.Get());     
00574   status = WPA_CLI_Command(command, output);
00575   if(!status) return false;
00576 
00577   GEN_XSLEEP.Seconds(3);
00578 
00579   command.Format(__L("%s scan_results") , prefixcommand.Get());     
00580   status = WPA_CLI_Command(command, output);
00581 
00582   int indexfr =  output.Find(__L("\n"), true);
00583   if(indexfr == XSTRING_NOTFOUND) return false;
00584 
00585   indexfr++;
00586 
00587   int index      = 0;
00588   int indexstart = 0;
00589 
00590   do{
00591       index =  output.Find(__L("\n"), true, indexfr);
00592       if(index == XSTRING_NOTFOUND ) break;
00593 
00594       for(int c=index; c>0 ; c--)
00595         {
00596           if(output.Get()[c] == __C(']'))  
00597             {
00598               indexstart = c;
00599               break;
00600             }               
00601         }
00602 
00603       indexfr = index+1;
00604 
00605       if(indexstart)
00606         {
00607           XSTRING* newstr = new XSTRING();
00608           if(newstr)
00609             {
00610               output.Copy(indexstart+1, index, (*newstr));
00611               newstr->DeleteCharacter(__C('\t'), XSTRINGCONTEXT_FROM_FIRST);
00612               
00613               nameAPs.Add(newstr);                 
00614             }
00615         }
00616 
00617     } while(index != XSTRING_NOTFOUND);
00618 
00619   return true;
00620 }
