00509 XWORD DIOMODBUS_CLIENT::CRC16(XBUFFER& data)
00510 {
00511   XWORD poly16 = 0xA001;
00512   XWORD LSB;
00513   XWORD crc    = 0xFFFF;
00514 
00515   for(int d=0; d<(int)data.GetSize(); d++)
00516     {
00517       crc = ((crc^data.Get()[d]) | 0xFF00) & (crc | 0x00FF);
00518 
00519       for(int c=0; c<8; c++)
00520         {
00521           LSB=(crc & 0x0001);
00522           crc=crc/2;
00523 
00524           if(LSB) crc = crc ^ poly16;
00525         }
00526     }
00527 
00528   SWAPWORD(crc);
00529 
00530   return crc;
00531 }
