00768       xtimer = NULL;
00769     }
00770 
00771   if(xfile)
00772     {
00773       xfile->Close();
00774       GEN_XFACTORY.Delete_File(xfile);
00775       xfile = NULL;
00776     }
00777 
00778   if(sendbuffer)
00779     {
00780       delete sendbuffer;
00781       sendbuffer = NULL;
00782     }
00783 
00784   SendFileList_DeleteAll();
00785 
