01073                                                           XBUFFER xbufferexchange;
01074                                                           (*obexpushfile->GetNameSend())->ConvertToBufferExchange(xbufferexchange, true, true);
01075                                                           sendbuffer->Add(xbufferexchange->Get(), wsize);  
01076 
01077                                                           sendbuffer->Add((XBYTE)0xC3);
01078                                                           sendbuffer->Add((XDWORD)filesize);
01079 
01080                                                           if(inlittleendian)
01081                                                                   wsize = SwapWORD((XWORD)sendbuffer->GetSize());
01082                                                             else  wsize = (XWORD)sendbuffer->GetSize();
01083                                                           sendbuffer->Set((XBYTE*)&wsize,2,1);
01084 
01085                                                           //XTRACE_PRINTDATABLOCK(2|XDEBUGTRACELEVEL_WITHCOLOR, (*sendbuffer));
01086 
01087                                                           sio->Write(sendbuffer->Get(),sendbuffer->GetSize());
01088                                                           sio->WaitToFlushOutXBuffer(5);
01089                                                           break;
01090 
01091               case DIOOBEXPUSH_XFSMSTATE_RECVHEADPUSH   : break;
01092 
01093               case DIOOBEXPUSH_XFSMSTATE_SENDBLOCKPUSH  : break;
01094 
01095               case DIOOBEXPUSH_XFSMSTATE_RECVBLOCKPUSH  : break;
01096 
01097               case DIOOBEXPUSH_XFSMSTATE_ENDPUSH        : if(xfile) xfile->Close();
01098 
01099                                                           if(!haveerror)
01100                                                             {
01101                                                               obexpushfile->SetIsSend(true);
01102                                                               obexpushfile->SetSendPercent(100);
01103                                                               obexpushfile->SetSendTimeElapsed(xtimer->GetMeasureMilliSeconds());
01104                                                               obexpushfile->SetError(DIOOBEXPUSHERROR_NONE);
01105 
01106                                                               if((indexfilelist+1<(int)sendfilelist.GetSize()))
01107                                                                 {
01108                                                                   indexfilelist++;
01109                                                                   SetEvent(DIOOBEXPUSH_XFSMEVENT_INIPUSH);
01110                                                                 }
01111                                                             }
01112                                                            else
01113                                                             {
01114                                                               for(int c=0;c<(int)sendfilelist.GetSize();c++)
01115                                                                 {
01116                                                                   obexpushfile = (DIOOBEXPUSHFILE*)sendfilelist.Get(c);
01117                                                                   if(obexpushfile)
01118                                                                     {
01119                                                                       obexpushfile->SetIsSend(false);
01120 
01121                                                                       if(sendisstart)
01122                                                                         {
01123                                                                           obexpushfile->SetError(DIOOBEXPUSHERROR_SENDING);
01124                                                                         }
01125                                                                        else
01126                                                                         {
01127                                                                           if(sio->IsRefusedConnection())
01128                                                                             {
01129                                                                               obexpushfile->SetError(DIOOBEXPUSHERROR_REFUSEDCONNECTION);
01130                                                                             }
01131                                                                            else
01132                                                                             {
01133                                                                               obexpushfile->SetError(DIOOBEXPUSHERROR_NOTCONNECTION);
01134                                                                             }
01135                                                                         }
01136                                                                     }
01137                                                                 }
01138 
01139                                                               return false;
01140                                                             }
01141                                                           break;
01142             }
01143         }
01144     }
01145 
01146   return true;
01147 }
