01161 bool DIOOBEXPUSH::ReadAnswer(XBUFFER& xbuffer)
01162 {
01163   if(!diostream) return false;
01164 
01165   int  sizeread;
01166 
01167   xbuffer.Resize(DIOOBEXPUSH_MAXANSWER);
01168 
01169   sizeread = diostream->Read((XBYTE*)xbuffer.Get(), xbuffer.GetSize());
01170   xbuffer.Resize(sizeread);
01171 
01172   return sizeread?true:false;
01173 }
