01187 void DIOOBEXPUSH::ThreadSend(void* data)
01188 {
01189   DIOOBEXPUSH* obexpush = (DIOOBEXPUSH*)data;
01190   if(!obexpush) return;
01191 
01192   obexpush->SetIsSending(obexpush->CheckSending());
01193 }
