00759 bool DIOPROTOCOL_FILE::ReadBlock(XBYTE* buffer)
00760 {
00761   if(!sourcename[0]) return false;
00762 
00763   if(!xfile->Open(sourcename)) return false;
00764 
00765   bool status = false;
00766 
00767   size = xfile->GetSize();
00768   if(size)
00769     {
00770       xfile->SetPosition(position);
00771 
00772       xfile->Read(buffer, &sizeblock);
00773       if(sizeblock)
00774         {
00775           xfile->GetPosition(position);
00776           if(size) percent = (XBYTE)(position*100/size);
00777 
00778           status = true;
00779         }
00780     }
00781 
00782   xfile->Close();
00783 
00784   return status;
00785 }
00786 
00787 
00799 bool DIOPROTOCOL_FILE::WriteBlock(XBYTE* buffer)
00800 {
00801   if(!xfile)         return false;
00802   if(!targetname[0]) return false;
00803 
00804   if(position)
00805     {
00806       if(!xfile->Open(targetname,false))
00807         {
00808           if(!xfile->Create(targetname)) return false;
00809         }
00810     }
00811    else
00812     {
00813       if(!xfile->Create(targetname)) return false;
00814     }
00815 
00816   bool status = false;
00817 
00818   if(sizeblock)
00819     {
00820       xfile->SetPosition((XDWORD)XFILE_SEEKEND);
00821 
00822       status = xfile->Write(buffer,sizeblock);
00823       if(status)
00824         {
00825           xfile->GetPosition(position);
00826           if(size) percent = (XBYTE)(position*100/size);
00827         }
00828     }
00829 
00830   xfile->Close();
00831 
00832   return status;
00833 }
