00918 DIOPROTOCOL::DIOPROTOCOL(DIOSTREAM* diostream)
00919 {
00920   Clean();
00921 
00922   this->diostream   = diostream;
00923 
00924   answers.SetIsMulti(true);
00925   asks.SetIsMulti(true);
00926 
00927   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_COMMAND);
00928   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_FILEBLOCK);
00929   RegisterEvent(DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
00930 
00931   GEN_XFACTORY_CREATE(xdatetime         , CreateDateTime())
00932   GEN_XFACTORY_CREATE(xtimer            , CreateTimer())
00933   GEN_XFACTORY_CREATE(watchdog          , CreateTimer())
00934   GEN_XFACTORY_CREATE(xdir              , Create_Dir())
00935   GEN_XFACTORY_CREATE(timerfileprogress , CreateTimer())
00936 
00937   AddCommand(DIOPROTOCOL_CMDTYPE_SENDISINITSERVERPROTOCOL   , __L("O")                , __L("O")            , RCV_SendIsInitServerProtocol      , __L("SendIsInitServerProtocol")   );
00938   AddCommand(DIOPROTOCOL_CMDTYPE_GETPROTOCOLVERSION         , __L("")                 , __L("WWW")          , RCV_GetProtocolVersion            , __L("GetProtocolVersion")         );
00939   AddCommand(DIOPROTOCOL_CMDTYPE_GETAPPLICATIONVERSION      , __L("")                 , __L("WWW")          , RCV_GetApplicationVersion         , __L("GetApplicationVersion")      );
00940   AddCommand(DIOPROTOCOL_CMDTYPE_GETAPPLICATIONNAME         , __L("")                 , __L("S")            , RCV_GetApplicationName            , __L("GetApplicationName")         );
00941   AddCommand(DIOPROTOCOL_CMDTYPE_PING                       , __L("")                 , __L("O")            , RCV_Ping                          , __L("Ping")                       );
00942   AddCommand(DIOPROTOCOL_CMDTYPE_GETDATETIME                , __L("")                 , __L("S")            , RCV_GetDateTime                   , __L("GetDateTime")                );
00943   AddCommand(DIOPROTOCOL_CMDTYPE_SETDATETIME                , __L("S")                , __L("O")            , RCV_SetDateTime                   , __L("SetDateTime")                );
00944   AddCommand(DIOPROTOCOL_CMDTYPE_INITFILEBLOCK              , __L("SSBD")             , __L("B")            , RCV_InitFileBlock                 , __L("FileInit")                   );
00945   AddCommand(DIOPROTOCOL_CMDTYPE_GETFILEBLOCK               , __L("")                 , __L("BDDDBAX")      , RCV_GetFileBlock                  , __L("GetFileBlock")               );
00946   AddCommand(DIOPROTOCOL_CMDTYPE_SENDFILEBLOCK              , __L("BDDDBAX")          , __L("B")            , RCV_SendFileBlock                 , __L("SendFileBlock")              );
00947   AddCommand(DIOPROTOCOL_CMDTYPE_DELETEFILE                 , __L("SB")               , __L("O")            , RCV_DeleteFile                    , __L("DeleteFile")                 );
00948   AddCommand(DIOPROTOCOL_CMDTYPE_MAKEDIR                    , __L("S")                , __L("O")            , RCV_MakeDir                       , __L("MakeDir")                    );
00949   AddCommand(DIOPROTOCOL_CMDTYPE_DELETEDIR                  , __L("SB")               , __L("O")            , RCV_DeleteDir                     , __L("DeleteDir")                  );
00950   AddCommand(DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT         , __L("SS")               , __L("SBDD")         , RCV_GetDirElement                 , __L("GetFirstDirElement")         );
00951   AddCommand(DIOPROTOCOL_CMDTYPE_GETNEXTDIRELEMENT          , __L("")                 , __L("SBDD")         , RCV_GetDirElement                 , __L("GetNextDirElement")          );
00952   #ifdef DIO_ALERTS_ACTIVE
00953   AddCommand(DIOPROTOCOL_CMDTYPE_SENDALERT                  , __L("SSDDDDBSSS")       , __L("B")            , RCV_SendAlert                     , __L("SendAlert")                  );
00954   #endif
00955 
00956   fileprogress = new DIOPROTOCOL_FILE();
00957   if(!fileprogress) return;
00958 
00959   GEN_XFACTORY_CREATE(xmutexanswers, Create_Mutex())
00960   if(!xmutexanswers) return;
00961 
00962   GEN_XFACTORY_CREATE(xmutexasks, Create_Mutex())
00963   if(!xmutexasks) return;
00964 
00965   GEN_XFACTORY_CREATE(xmutexcommands, Create_Mutex())
00966   if(!xmutexcommands) return;
00967 
00968   GEN_XFACTORY_CREATE(xmutexreads,Create_Mutex())
00969   if(!xmutexreads) return;
00970 
00971   GEN_XFACTORY_CREATE(xmutexwrites, Create_Mutex())
00972   if(!xmutexwrites) return;
00973 
00974   GEN_XFACTORY_CREATE(xmutexfilesprogress, Create_Mutex())
00975   if(!xmutexfilesprogress) return;
00976 
00977   xthreadreceived = CREATEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, __L("DIOPROTOCOL::DIOPROTOCOL received"),ThreadReceived,(void*)this);
00978   if(xthreadreceived)
00979     {
00980       if(!xthreadreceived->Ini()) return;
00981     }
00982 
00983   xthreadasks = CREATEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, __L("DIOPROTOCOL::DIOPROTOCOL ask"), ThreadAsks, (void*)this);
00984   if(xthreadasks)
00985     {
00986       if(!xthreadasks->Ini()) return;
00987     }
00988 }
