01001 DIOPROTOCOL::~DIOPROTOCOL()
01002 {
01003   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_COMMAND);
01004   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_FILEBLOCK);
01005   DeRegisterEvent(DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
01006 
01007   doexit = true;
01008 
01009   if(xthreadreceived)
01010     {
01011       xthreadreceived->End();
01012       DELETEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, xthreadreceived);
01013       xthreadreceived = NULL;
01014     }
01015 
01016   if(xthreadasks)
01017     {
01018       xthreadasks->End();
01019       DELETEXTHREAD(XTHREADGROUPID_DIOPROTOCOL, xthreadasks);
01020       xthreadasks = NULL;
01021     }
01022 
01023   DeleteAllAnswers();
01024   DeleteAllAsks();
01025 
01026   DeleteAllCommands();
01027 
01028   GEN_XFACTORY.Delete_Mutex(xmutexanswers);
01029   GEN_XFACTORY.Delete_Mutex(xmutexasks);
01030   GEN_XFACTORY.Delete_Mutex(xmutexcommands);
01031   GEN_XFACTORY.Delete_Mutex(xmutexreads);
01032   GEN_XFACTORY.Delete_Mutex(xmutexwrites);
01033   GEN_XFACTORY.Delete_Mutex(xmutexfilesprogress);
01034 
01035   delete fileprogress;
01036 
01037   if(xdir)              GEN_XFACTORY.Delete_Dir(xdir);
01038   if(watchdog)          GEN_XFACTORY.DeleteTimer(watchdog);
01039   if(xtimer)            GEN_XFACTORY.DeleteTimer(xtimer);
01040   if(xdatetime)         GEN_XFACTORY.DeleteDateTime(xdatetime);
01041   if(timerfileprogress) GEN_XFACTORY.DeleteTimer(timerfileprogress);
01042 
01043   Clean();
01044 }
