01443 bool DIOPROTOCOL::AddCommand(XDWORD type, XCHAR* inmask, XCHAR* outmask, DIOPROTOCOL_RECEIVEDFUNC receivedfunc, XCHAR* description)
01444 {
01445   DIOPROTOCOL_COMMAND* cmd = new DIOPROTOCOL_COMMAND(type,inmask,outmask,receivedfunc,description);
01446   if(!cmd) return false;
01447 
01448   commands.Add(cmd);
01449 
01450   return true;
01451 }
