01465 DIOPROTOCOL_COMMAND* DIOPROTOCOL::GetCommand(XDWORD type)
01466 {
01467   for(int c=0;c<(int)commands.GetSize();c++)
01468     {
01469       DIOPROTOCOL_COMMAND* cmd = (DIOPROTOCOL_COMMAND*)commands.Get(c);
01470       if(cmd)
01471         {
01472           if(cmd->GetType() == type) return cmd;
01473         }
01474     }
01475 
01476   return NULL;
01477 }
