01493 DIOPROTOCOL_ANSWER* DIOPROTOCOL::AddAnswer(XDWORD type, XDWORD ID, XBUFFER& xbuffer)
01494 {
01495   if(xmutexanswers) xmutexanswers->Lock();
01496 
01497   DIOPROTOCOL_ANSWER* answer = new DIOPROTOCOL_ANSWER(type, ID, xbuffer);
01498   if(answer) answers.Add(answer);
01499 
01500   if(xmutexanswers) xmutexanswers->UnLock();
01501 
01502   return answer;
01503 }
