01505 
01518 DIOPROTOCOL_ANSWER* DIOPROTOCOL::GetAnswer(XDWORD type, XDWORD ID)
01519 {
01520   if(xmutexanswers) xmutexanswers->Lock();
01521 
01522   DIOPROTOCOL_ANSWER* answer = NULL;
01523 
01524   for(XDWORD c=0; c<answers.GetSize(); c++)
01525     {
01526       answer = (DIOPROTOCOL_ANSWER*)answers.Get(c);
01527       if(answer)
01528         {
01529           if((answer->GetType() == type) && (answer->GetID()  == ID))  break; else answer = NULL;
01530         }
01531     }
01532 
01533   if(xmutexanswers) xmutexanswers->UnLock();
01534 
01535   return answer;
01536 }
01537 
01538 
01551 bool DIOPROTOCOL::DeleteAnswer(XDWORD type, XDWORD ID)
01552 {
01553   if(xmutexanswers) xmutexanswers->Lock();
01554 
01555   for(XDWORD c=0; c<answers.GetSize(); c++)
01556     {
01557       DIOPROTOCOL_ANSWER* answer = (DIOPROTOCOL_ANSWER*)answers.Get(c);
01558       if(answer)
01559         {
01560           if((answer->GetType() == type) && (answer->GetID() == ID))
01561             {
01562               answers.Delete(answer);
01563               delete answer;
01564 
01565               break;
01566             }
01567         }
01568     }
01569 
01570   if(xmutexanswers) xmutexanswers->UnLock();
01571 
01572   return true;
01573 }
01574 
