01590 
01591   if(!answers.IsEmpty())
01592     {
01593       answers.DeleteContents();
01594       answers.DeleteAll();
01595 
01596       status = true;
01597     }
01598 
01599   if(xmutexanswers) xmutexanswers->UnLock();
01600 
01601   return status;
01602 }
