01709 
01710 
01724 int DIOPROTOCOL::ReadCommand(XDWORD& type, XDWORD& ID, XBUFFER& xbuffer)
01725 {
01726   if(!IsConnected()) return 0;
01727 
01728   XDWORD sizecmd = diostream->GetInXBuffer()->GetSize();
01729   if(sizecmd < DIOPROTOCOL_SIZEOFHEAD) return 0;
01730 
01731   XBYTE head[DIOPROTOCOL_SIZEOFHEAD];
01732 
01733   memset(head ,0 , DIOPROTOCOL_SIZEOFHEAD);
01734   diostream->GetInXBuffer()->Get(head, DIOPROTOCOL_SIZEOFHEAD);
01735 
01736   HASHCRC32 crc32;
01737   XDWORD*   headcmd         = (XDWORD*)head;
01738   XDWORD    IDhead          = 0;
01739   XDWORD    sizebuffer      = 0;
01740   XDWORD    bodycrc32value  = 0;
01741   XDWORD    headcrc32value  = 0;
01742   XDWORD    crc32value      = 0;
01743 
01744   IDhead          = headcmd[0];
01745   type            = headcmd[1];
01746   sizebuffer      = headcmd[2];
01747   ID              = headcmd[3];
01748   bodycrc32value  = headcmd[4];
01749   headcrc32value  = headcmd[5];
01750 
01751   crc32.ResetResult();
01752   crc32.Do((XBYTE*)headcmd, (DIOPROTOCOL_SIZEOFHEAD - sizeof(XDWORD)));
01753   crc32value  = crc32.GetResultCRC32();
01754 
01755   if((IDhead != DIOPROTOCOL_MSGIDHEAD) || (headcrc32value != crc32value))
01756     {
01757       return 0;
01758     }
01759 
01760   if(!diostream->WaitToFilledReadingBuffer((DIOPROTOCOL_SIZEOFHEAD + sizebuffer), 5))
01761     {
01762       return 0;
01763     }
01764 
01765   //diostream->GetInXBuffer()->Extract(NULL, 0, DIOPROTOCOL_SIZEOFHEAD);
01766   diostream->Read(head, DIOPROTOCOL_SIZEOFHEAD);
01767 
01768   if(sizebuffer)
01769     {
01770       xbuffer.Delete();
01771       xbuffer.Resize(sizebuffer);
01772 
01773     //if(diostream->GetInXBuffer()->Extract(xbuffer.Get(), 0, sizebuffer))
01774       if(diostream->Read(xbuffer.Get(), sizebuffer))
01775         {
01776           crc32.ResetResult();
01777           crc32.Do(xbuffer);
01778           crc32value  = crc32.GetResultCRC32();
01779 
01780           if(bodycrc32value == crc32value)
01781             {
01782               return (DIOPROTOCOL_SIZEOFHEAD + sizebuffer);
01783             }
01784            else
01785             {
01786               return 0;
01787             }
01788 
01789         }
01790        else
01791         {
01792           return 0;
01793         }
01794     }
01795 
01796   return DIOPROTOCOL_SIZEOFHEAD;
01797 }
01798 
01799 
01809 DIOPROTOCOL_RESULT DIOPROTOCOL::Received()
01810 {
01811   if(!IsConnected()) return DIOPROTOCOL_RESULT_NOTCONNEX;
01812 
01813   if(xmutexreads) xmutexreads->Lock();
01814 
01815   int                 size = 0;
01816   DIOPROTOCOL_RESULT  status = DIOPROTOCOL_RESULT_UNKNOWNCMD;
01817 
01818   XDWORD                type   = DIOPROTOCOL_CMDTYPE_NONE;
01819   XDWORD                ID     = 0;
01820   XBUFFER               xbuffer;
01821 
01822   size = ReadCommand(type, ID, xbuffer);
01823   if(size)
01824     {
01825       bool   isanswer = ((type&DIOPROTOCOL_CMDTYPE_ANSWER)==DIOPROTOCOL_CMDTYPE_ANSWER)?true:false;
01826       XDWORD rtype    = (type&~DIOPROTOCOL_CMDTYPE_ANSWER);
01827 
01828       /*
01829       DIOPROTOCOL_COMMAND* cmd  = GetCommand(rtype);
01830       if(cmd)
01831         {
01832          XTRACE_PRINTCOLOR(((isanswer)?1:2), __L("DIOPROTOCOL %s %s : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
01833                                                                                                     , (isanswer)?__L("Answer  "):__L("Ask     ")
01834                                                                                                     , this
01835                                                                                                     , ID
01836                                                                                                     , rtype
01837                                                                                                     , cmd->GetDescription()
01838                                                                                                     , xbuffer.GetSize());
01839         }
01840       */
01841       if(isanswer)
01842         {
01843           if(AddAnswer(rtype, ID, xbuffer)) status = DIOPROTOCOL_RESULT_OK;
01844         }
01845        else
01846         {
01847           //status = ReceivedHandle(rtype, ID, xbuffer);
01848           if(AddAsk(rtype, ID, xbuffer))    status = DIOPROTOCOL_RESULT_OK;
01849         }
01850 
01851     }
01852 
01853   if(xmutexreads) xmutexreads->UnLock();
01854 
01855   return status;
01856 }
01857 
01858 
01872 DIOPROTOCOL_RESULT DIOPROTOCOL::ReceivedHandle(XDWORD type, XDWORD ID, XBUFFER& xbuffer)
01873 {
01874   DIOPROTOCOL_RESULT status     = DIOPROTOCOL_RESULT_UNKNOWNCMD;
01875   XDWORD             param      = 0;
01876 
01877   if((type & DIOPROTOCOL_CMDTYPE_ANSWER) == DIOPROTOCOL_CMDTYPE_ANSWER) return DIOPROTOCOL_RESULT_UNKNOWNCMD;
01878 
01879   DIOPROTOCOL_COMMAND* cmd = GetCommand(type);
01880   if(!cmd)  return DIOPROTOCOL_RESULT_UNKNOWNCMD;
01881 
01882   DIOPROTOCOL_CONNECTION* connection =  (DIOPROTOCOL_CONNECTION*)GetConnection();
01883 
01884   if(connection) connection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_ANSWER);
01885 
01886   if(cmd->GetReceivedFunc())
01887     {
01888       int ssize = cmd->GetReceivedFunc()(this, cmd, xbuffer, ID, param);
01889 
01890       if(ssize != DIOPROTOCOL_ERRORRECEIVED)
01891         {
01892           if(cmd->GetSOUTMask()->GetSize())
01893             {
01894               if(ssize)
01895                 {
01896                   if(SendCommand(type|DIOPROTOCOL_CMDTYPE_ANSWER, ID, xbuffer)) status = DIOPROTOCOL_RESULT_OK;
01897                 }
01898 
01899             } else status = DIOPROTOCOL_RESULT_OK;
01900         }
01901     }
01902 
01903   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, type, (status==DIOPROTOCOL_RESULT_OK)?true:false, NULL, param);
01904 
01905   if(connection) connection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_ANSWER);
01906 
01907   return status;
01908 }
01909 
01910 
01924 bool DIOPROTOCOL::SendCommand(XDWORD type, XDWORD& ID, XBUFFER& xbuffer)
01925 {
01926   if(!IsConnected()) return false;
01927 
01928   HASHCRC32 crc32;
01929   XDWORD    crc32head = 0;
01930   XDWORD    crc32body = 0;
01931 
01932   DIOPROTOCOL_COMMAND* cmd = GetCommand(type&~DIOPROTOCOL_CMDTYPE_ANSWER);
01933   if(!cmd) return false;
01934 
01935   if(xmutexwrites) xmutexwrites->Lock();
01936 
01937   XBYTE   head[DIOPROTOCOL_SIZEOFHEAD];
01938   XDWORD* ptr_head  = (XDWORD*)head;
01939 
01940   memset(head, 0, DIOPROTOCOL_SIZEOFHEAD);
01941 
01942   if(xbuffer.GetSize())
01943     {
01944       crc32.ResetResult();
01945       crc32.Do(xbuffer);
01946       crc32body = crc32.GetResultCRC32();
01947     }
01948 
01949   if(!ID)
01950     {
01951       ID = IDmsgcounter;
01952       ID |= maskID;
01953 
01954       IDmsgcounter++;
01955       if(IDmsgcounter > DIOPROTOCOL_CMDTYPE_SECUENCIALLIMIT)  IDmsgcounter = 0;
01956     }
01957 
01958   ptr_head[0] = DIOPROTOCOL_MSGIDHEAD;
01959   ptr_head[1] = type;
01960   ptr_head[2] = xbuffer.GetSize();
01961   ptr_head[3] = ID;
01962   ptr_head[4] = crc32body;
01963 
01964   crc32.ResetResult();
01965   crc32.Do(head, DIOPROTOCOL_SIZEOFHEAD-sizeof(XDWORD));
01966   crc32head = crc32.GetResultCRC32();
01967 
01968   ptr_head[5] = crc32head;
01969 
01970   bool status = false;
01971 
01972   if(xbuffer.GetSize())
01973     {
01974       XBUFFER xbuffersend;
01975 
01976       xbuffersend.Add(head, DIOPROTOCOL_SIZEOFHEAD);
01977       xbuffersend.Add(xbuffer);
01978 
01979       status = (diostream->Write(xbuffersend) == xbuffersend.GetSize())?true:false;
01980       if(status) diostream->WaitToFlushOutXBuffer(5);
01981     }
01982    else
01983     {
01984       status = (diostream->Write(head, DIOPROTOCOL_SIZEOFHEAD) == DIOPROTOCOL_SIZEOFHEAD)?true:false;
01985       if(status) status = diostream->WaitToFlushOutXBuffer(5);
01986     }
01987 
01988   if(xmutexwrites) xmutexwrites->UnLock();
01989 
01990   return status;
01991 }
01992 
01993 
02007 bool DIOPROTOCOL::Command(XDWORD type, XBUFFER& xbuffer, int timeout)
02008 {
02009   if(!IsConnected())  return false;
02010   if(!watchdog)       return false;
02011 
02012   if(xmutexcommands) xmutexcommands->Lock();
02013 
02014   XDWORD ID     = 0;
02015   bool   status = false;
02016 
02017 
02018   DIOPROTOCOL_COMMAND* cmd = GetCommand(type);
02019   if(cmd)
02020     {
02021       /*
02022      XTRACE_PRINTCOLOR(1, __L("DIOPROTOCOL %s Command  : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
02023                                                                                        , this
02024                                                                                        , ID
02025                                                                                        , type
02026                                                                                        , cmd->GetDescription()
02027                                                                                        , xbuffer.GetSize());
02028       */
02029       if(SendCommand(type, ID, xbuffer))
02030         {
02031           /*
02032          XTRACE_PRINTCOLOR(1, __L("DIOPROTOCOL %s Send     : %08X %08X %02d %-35s (%8d)") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
02033                                                                                            , this
02034                                                                                            , ID
02035                                                                                            , type
02036                                                                                            , cmd->GetDescription()
02037                                                                                            , xbuffer.GetSize());
02038           */
02039           xbuffer.Delete();
02040 
02041           //--------------------------------------------
02042           // No Answer
02043           if(!cmd->GetSOUTMask()->IsEmpty())
02044             {
02045               watchdog->Reset();
02046 
02047               while(1)
02048                 {
02049                   if(!IsConnected()) break;
02050 
02051                   DIOPROTOCOL_ANSWER* answer = GetAnswer(type, ID);
02052                   if(answer)
02053                     {
02054                       xbuffer.Delete();
02055                       xbuffer.Add(answer->GetBuffer());
02056 
02057                       DeleteAnswer(type, ID);
02058 
02059                       status = true;
02060                       break;
02061                     }
02062 
02063                   if((int)watchdog->GetMeasureSeconds() >= timeout)
02064                     {
02065                       break;
02066                     }
02067 
02068                   GEN_XSLEEP.MilliSeconds(10);
02069                 }
02070 
02071             } else status = true;
02072 
02073           if(!status)
02074             {
02075              XTRACE_PRINTCOLOR((status?1:4), __L("DIOPROTOCOL %s  : %08X %08X %02d %-35s (%8d)  %s") , GetDIOStream()->GetConfig()->IsServer()?__L("Server  "):__L("Client  ")
02076                                                                                                               , this
02077                                                                                                               , ID
02078                                                                                                               , type
02079                                                                                                               , cmd->GetDescription()
02080                                                                                                               , xbuffer.GetSize()
02081                                                                                                               , status?__L("Ok"):__L("ERROR!"));
02082             }
02083         }
02084 
02085     }
02086 
02087   if(xmutexcommands) xmutexcommands->UnLock();
02088 
02089   return status;
02090 }
02091 
