02297 bool DIOPROTOCOL::SetFileProgress(DIOPROTOCOL_DIRECTION direction, XPATH& sourcename, XPATH& targetname, XDWORD CRC32)
02298 {
02299   if(sourcename.IsEmpty())  return false;
02300 
02301   fileprogress->Reset();
02302 
02303   fileprogress->SetSourceName(sourcename);
02304 
02305   if(targetname.IsEmpty())
02306          fileprogress->SetTargetName(sourcename);
02307     else fileprogress->SetTargetName(targetname);
02308 
02309   fileprogress->SetActive(false);
02310   fileprogress->SetDirection(direction);
02311   fileprogress->SetPosition(0);
02312 
02313   if(CRC32)
02314         fileprogress->SetCRC32(CRC32);
02315    else fileprogress->SetCRC32(CRC32OfFile(sourcename));
02316 
02317   return true;
02318 }
