02380 
02394 bool DIOPROTOCOL::CMD_GetProtocolVersion(XWORD& version, XWORD& subversion, XWORD& subversionerr)
02395 {
02396   bool result = false;
02397 
02398   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_GETPROTOCOLVERSION);
02399   if(!cmd) return false;
02400 
02401   XBUFFER xbuffer;
02402 
02403   if(Command(cmd->GetType(), xbuffer))
02404     {
02405       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &version, &subversion, &subversionerr);
02406       result = true;
02407     }
02408 
02409   XDWORD param = (version<<8)|subversion;
02410 
02411   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, cmd->GetType(), result, NULL, param);
02412 
02413   return result;
02414 }
