02551 
02564 bool DIOPROTOCOL::CMD_Ping(XDWORD& milliseconds, int timeout)
02565 {
02566   if(!xtimer) return false;
02567 
02568   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_PING);
02569   if(!cmd) return false;
02570 
02571   XBUFFER xbuffer;
02572   XBYTE result = 0;
02573 
02574   xtimer->Reset();
02575 
02576   if(Command(cmd->GetType(), xbuffer, timeout))
02577     {
02578       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result);
02579     }
02580 
02581   milliseconds = (XDWORD)xtimer->GetMeasureMilliSeconds();
02582 
02583   SendEventCommand(this, DIOPROTOCOL_DIRECTION_SEND, cmd->GetType(),result?true:false,NULL,milliseconds);
02584 
02585   return result?true:false;
02586 }
