02808 
02824 bool DIOPROTOCOL::CMD_File(DIOPROTOCOL_DIRECTION direction, XCHAR* sourcename, XCHAR* targetname, int timeout, void* param)
02825 {
02826   if(!xtimer)    return false;
02827 
02828   if(!sourcename) return 0;
02829   if(!targetname) return 0;
02830 
02831   XPATH _sourcename;
02832   XPATH _targetname;
02833 
02834   _sourcename = sourcename;
02835   _targetname = targetname;
02836 
02837   if(_sourcename.IsEmpty()) return false;
02838   if(_targetname.IsEmpty()) return false;
02839 
02840   if(!SetFileProgress(direction,_sourcename,_targetname,0)) return false;
02841 
02842   if(!CMD_InitFileBlock(fileprogress)) return false;
02843 
02844   bool status  = false;
02845 
02846   SendEventFileBlock(DIOPROTOCOL_FILESTATUS_REQUEST , fileprogress->IsActive()
02847                                                     , fileprogress->GetSourceName()
02848                                                     , fileprogress->GetTargetName()
02849                                                     , fileprogress->GetPercent()
02850                                                     , param);
02851   xtimer->Reset();
02852 
02853   while(1)
02854     {
02855       if(fileprogress->IsActive())
02856         {
02857           switch(fileprogress->GetDirection())
02858             {
02859               case DIOPROTOCOL_DIRECTION_UNKNOWN  : fileprogress->SetActive(false);     break;
02860               case DIOPROTOCOL_DIRECTION_GET      : CMD_GetFileBlock(fileprogress);     break;
02861               case DIOPROTOCOL_DIRECTION_SEND     : CMD_SendFileBlock(fileprogress);    break;
02862             }
02863         }
02864 
02865       SendEventFileBlock(DIOPROTOCOL_FILESTATUS_WAIT , fileprogress->IsActive()
02866                                                      , fileprogress->GetSourceName()
02867                                                      , fileprogress->GetTargetName()
02868                                                      , fileprogress->GetPercent()
02869                                                      , param);
02870 
02871       GEN_XSLEEP.MilliSeconds(10);
02872 
02873       if(!IsConnected())                              break;
02874       if((int)xtimer->GetMeasureSeconds()>=timeout)   break;
02875       if(!fileprogress->IsActive())                   break;
02876     }
02877 
02878   if(fileprogress->GetPercent()>=100)  status = true;
02879 
02880   DIOPROTOCOL_FILESTATUS fstatus = DIOPROTOCOL_FILESTATUS_END;
02881   if(!status) fstatus = DIOPROTOCOL_FILESTATUS_ERROR;
02882 
02883   SendEventFileBlock(fstatus , fileprogress->IsActive()
02884                              , fileprogress->GetSourceName()
02885                              , fileprogress->GetTargetName()
02886                              , fileprogress->GetPercent()
02887                              , param);
02888   return status;
02889 }
02890 
