03102 
03119 bool DIOPROTOCOL::CMD_GetDirElement(XCHAR* pathsearch, XCHAR* mask, XPATH& namefile, XBYTE& type, int& sizefile, XDWORD& CRC32)
03120 {
03121   if(!pathsearch) return false;
03122   if(!mask)       return false;
03123 
03124   XBUFFER   xbuffer;
03125   XPATH     _pathsearch;
03126   XSTRING   _mask;
03127   bool      result      = false;
03128 
03129   _pathsearch = pathsearch;
03130   _mask       = mask;
03131 
03132   DIOPROTOCOL_COMMAND* cmd = GetCommand((!_pathsearch.IsEmpty())?DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT:DIOPROTOCOL_CMDTYPE_GETNEXTDIRELEMENT);
03133   if(!cmd) return false;
03134 
03135   if(_mask.IsEmpty()) _mask = __L("*");
03136 
03137   xbuffer.AddWithMask(cmd->GetSINMask(), &_pathsearch, &_mask);
03138 
03139   if(Command(cmd->GetType(), xbuffer))
03140     {
03141       namefile.Empty();
03142 
03143       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &namefile, &type, &sizefile, &CRC32);
03144 
03145       if(!namefile.IsEmpty()) result = true;
03146     }
03147 
03148   SendEventCommand(this, DIOPROTOCOL_DIRECTION_GET, cmd->GetType(), result?true:false, NULL, 0);
03149 
03150   return result?true:false;
03151 }
03152 
03153 
03169 int DIOPROTOCOL::RCV_GetDirElement(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03170 {
03171   XPATH     pathsearch;
03172   XSTRING   masksearch;
03173   XPATH     pathfile;
03174   XBYTE     typefile     = 0;
03175   int       sizefile     = 0;
03176   XDWORD    crc32value  = 0;
03177   bool      found       = false;
03178 
03179   pathfile.Empty();
03180 
03181   if(cmd->GetType()==DIOPROTOCOL_CMDTYPE_GETFIRSTDIRELEMENT)
03182     {
03183       pathsearch.Empty();
03184 
03185       xbuffer.GetWithMask(cmd->GetSINMask(), 0, &pathsearch, &masksearch);
03186 
03187       protocol->GetPathSearch()->Set(pathsearch);
03188       protocol->GetSearchElement()->DeleteFindFileData();
03189 
03190       found = protocol->GetXDir()->FirstSearch(pathsearch, masksearch, protocol->GetSearchElement());
03191     }
03192    else found =  protocol->GetXDir()->NextSearch(protocol->GetSearchElement());
03193 
03194   if(found)
03195     {
03196       typefile    = (XBYTE)protocol->GetSearchElement()->GetType();
03197 
03198       if(protocol->GetSearchElement()->GetType() == XDIRELEMENTTYPE_FILE)
03199         {
03200           pathfile  = protocol->GetPathSearch()->Get();
03201           pathfile += protocol->GetSearchElement()->GetNameFile()->Get();
03202 
03203           HASHCRC32 crc32;
03204 
03205           if(crc32.Do(pathfile, sizefile)) crc32value = crc32.GetResultCRC32();
03206         }
03207 
03208       pathfile  = protocol->GetSearchElement()->GetNameFile()->Get();
03209     }
03210 
03211   xbuffer.Delete();
03212   return xbuffer.AddWithMask(cmd->GetSOUTMask(), &pathfile, typefile, sizefile, crc32value);
03213 }
03214 
