03225 bool DIOPROTOCOL::DeleteAllCommands()
03226 {
03227   if(commands.IsEmpty())  return false;
03228 
03229   commands.DeleteContents();
03230   commands.DeleteAll();
03231 
03232   return true;
03233 }
03234 
03235 
03247 XDWORD DIOPROTOCOL::CRC32OfFile(XPATH& xpath)
03248 {
03249   XDWORD value = 0;
03250 
03251   HASHCRC32* crc32 = new HASHCRC32();
03252   if(!crc32) return false;
03253 
03254   XFILE* GEN_XFACTORY_CREATE(file, Create_File());
03255   if(file)
03256     {
03257       if(file->Open(xpath))
03258         {
03259           crc32->ResetResult();
03260           crc32->Do(file, file->GetSize(), 0);
03261           value  = crc32->GetResultCRC32();
03262 
03263           file->Close();
03264         }
03265 
03266       GEN_XFACTORY.Delete_File(file);
03267     }
03268 
03269   delete crc32;
03270 
03271   return value;
03272 }
03273 
03274 
03286 bool DIOPROTOCOL::CMD_InitFileBlock(DIOPROTOCOL_FILE* fp)
03287 {
03288   if(!fp) return false;
03289 
03290   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_INITFILEBLOCK);
03291   if(!cmd) return false;
03292 
03293   XBUFFER xbuffer;
03294   bool    result = false;
03295 
03296   xbuffer.AddWithMask(cmd->GetSINMask(), fp->GetSourceName()
03297                                        , fp->GetTargetName()
03298                                        , fp->GetDirection()
03299                                        , fp->GetCRC32());
03300 
03301 
03302   if(Command(cmd->GetType(), xbuffer))
03303     {
03304       if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result))
03305         {
03306           if(result) GetFileProgress()->SetActive(true);
03307         }
03308     }
03309 
03310   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), result, NULL, 0);
03311 
03312   return result;
03313 }
03314 
03315 
03331 int DIOPROTOCOL::RCV_InitFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03332 {
03333   XPATH                  sourcename;
03334   XPATH                  targetname;
03335   XBYTE                  _direction   = 0;
03336   DIOPROTOCOL_DIRECTION  direction    = (DIOPROTOCOL_DIRECTION)_direction;
03337   XDWORD                 CRC32        = 0;
03338   bool                   result       = false;
03339 
03340   xbuffer.GetWithMask(cmd->GetSINMask(), 0, &sourcename, &targetname, &_direction, &CRC32);
03341 
03342   direction = (DIOPROTOCOL_DIRECTION)_direction;
03343 
03344   if(protocol->SetFileProgress(direction, sourcename, targetname, CRC32))
03345     {
03346       protocol->GetFileProgress()->SetActive(true);
03347       result = true;
03348     }
03349 
03350   xbuffer.Delete();
03351   return xbuffer.AddWithMask(cmd->GetSOUTMask(), result);
03352 }
03353 
03354 
03366 bool DIOPROTOCOL::CMD_GetFileBlock(DIOPROTOCOL_FILE* fp)
03367 {
03368   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_GETFILEBLOCK);
03369   if(!cmd) return false;
03370 
03371   if(!fp) return false;
03372 
03373   if(!fp->IsActive()) return false;
03374 
03375   XBUFFER xbuffer;
03376   bool    status = false;
03377 
03378   if(Command(cmd->GetType(), xbuffer))
03379     {
03380       XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03381       if(filebuffer)
03382         {
03383           XBYTE   islastblock   = 0;
03384           XDWORD  size          = 0;
03385           XDWORD  crc32         = 0;
03386           XDWORD  sizeblock     = DIOPROTOCOL_MAXFILEBLOCK;
03387           XBYTE   percent       = 0;
03388 
03389           memset(filebuffer, 0, DIOPROTOCOL_MAXFILEBLOCK);
03390 
03391           if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0  , &islastblock
03392                                                         , &size
03393                                                         , &crc32
03394                                                         , &sizeblock
03395                                                         , &percent
03396                                                         , filebuffer
03397                                                         , &sizeblock))
03398 
03399             {
03400               fp->SetSize(size);
03401               fp->SetCRC32(crc32);
03402               fp->SetSizeBlock(sizeblock);
03403 
03404               if(fp->GetSizeBlock()) status = fp->WriteBlock(filebuffer);
03405 
03406               if(islastblock)
03407                 {
03408                   XDWORD valuecrc = CRC32OfFile((*fp->GetTargetName()));
03409                   if(crc32!=valuecrc) fp->Erase();
03410 
03411                   fp->SetActive(false);
03412                 }
03413             }
03414 
03415           delete[] filebuffer;
03416         }
03417     }
03418 
03419   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), status, NULL, 0);
03420 
03421   return status;
03422 }
03423 
03424 
03440 int DIOPROTOCOL::RCV_GetFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03441 {
03442   DIOPROTOCOL_FILE*   fp;
03443   int                 ssize  = 0;
03444   bool                status = false;
03445 
03446   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03447   if(filebuffer)
03448     {
03449       memset(filebuffer, 0, DIOPROTOCOL_MAXFILEBLOCK);
03450 
03451       fp = protocol->GetFileProgress();
03452       if(fp)
03453         {
03454           XBYTE islastblock = 0;
03455 
03456           fp->SetSizeBlock(DIOPROTOCOL_MAXFILEBLOCK);
03457 
03458           fp->ReadBlock(filebuffer);
03459 
03460           //if(fp->GetSizeBlock()!=DIOPROTOCOL_MAXFILEBLOCK)  islastblock = 1;
03461           if(fp->GetSize() == fp->GetPosition()) islastblock = 1;
03462 
03463           if(fp->GetSizeBlock())
03464             {
03465               xbuffer.Delete();
03466               ssize = xbuffer.AddWithMask(cmd->GetSOUTMask(), islastblock
03467                                                             , fp->GetSize()
03468                                                             , fp->GetCRC32()
03469                                                             , fp->GetSizeBlock()
03470                                                             , fp->GetPercent()
03471                                                             , filebuffer
03472                                                             , fp->GetSizeBlock());
03473 
03474 
03475 
03476               param  = 1;
03477               status = true;
03478             }
03479 
03480           if(islastblock) fp->SetActive(false);
03481         }
03482 
03483       delete[] filebuffer;
03484     }
03485 
03486   if(!status) ssize = 0;
03487 
03488   return ssize;
03489 }
03490 
03491 
03503 bool DIOPROTOCOL::CMD_SendFileBlock(DIOPROTOCOL_FILE* fp)
03504 {
03505   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_SENDFILEBLOCK);
03506   if(!cmd) return false;
03507 
03508   if(!fp) return false;
03509 
03510   if(!fp->IsActive()) return false;
03511 
03512   XBUFFER xbuffer;
03513   bool    status = false;
03514 
03515 
03516   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03517   if(filebuffer)
03518     {
03519       XBYTE islastblock = 0;
03520 
03521       fp->SetSizeBlock(DIOPROTOCOL_MAXFILEBLOCK);
03522 
03523       fp->ReadBlock(filebuffer);
03524 
03525       //if(fp->GetSizeBlock()!=DIOPROTOCOL_MAXFILEBLOCK)  islastblock = 1;
03526       if(fp->GetSize() == fp->GetPosition()) islastblock = 1;
03527 
03528       if(fp->GetSizeBlock())
03529         {
03530           xbuffer.Delete();
03531           XDWORD size = xbuffer.AddWithMask(cmd->GetSINMask(), islastblock
03532                                                              , fp->GetSize()
03533                                                              , fp->GetCRC32()
03534                                                              , fp->GetSizeBlock()
03535                                                              , fp->GetPercent()
03536                                                              , filebuffer
03537                                                              , fp->GetSizeBlock());
03538 
03539 
03540           if(Command(cmd->GetType(), xbuffer))
03541             {
03542               XBYTE result = 0;
03543 
03544               if(xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result))
03545                 {
03546                   if(result) status = true;
03547                 }
03548 
03549               if(islastblock) fp->SetActive(false);
03550             }
03551         }
03552 
03553       delete[] filebuffer;
03554     }
03555 
03556   SendEventCommand(this, fp->GetDirection(), cmd->GetType(), status, NULL, 0);
03557 
03558   return status;
03559 }
03560 
03561 
03577 int DIOPROTOCOL::RCV_SendFileBlock(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03578 {
03579   XDWORD  filesize    = 0;
03580   XDWORD  crc32       = 0;
03581   XDWORD  sizeblock   = DIOPROTOCOL_MAXFILEBLOCK;
03582   XBYTE percent     = 0;
03583   int   ssize       = 0;
03584   bool  status      = false;
03585 
03586   XBYTE* filebuffer = new XBYTE[DIOPROTOCOL_MAXFILEBLOCK];
03587   if(filebuffer)
03588     {
03589       XBYTE islastblock = 0;
03590 
03591       memset(filebuffer,0,DIOPROTOCOL_MAXFILEBLOCK);
03592 
03593       xbuffer.GetWithMask(cmd->GetSINMask(), 0, &islastblock
03594                                               , &filesize
03595                                               , &crc32
03596                                               , &sizeblock
03597                                               , &percent
03598                                               , filebuffer
03599                                               , &sizeblock);
03600 
03601       DIOPROTOCOL_FILE* fp = protocol->GetFileProgress();
03602       if(fp)
03603         {
03604           fp->SetSize(filesize);
03605           fp->SetCRC32(crc32);
03606           fp->SetSizeBlock(sizeblock);
03607 
03608           fp->WriteBlock(filebuffer);
03609 
03610           if(fp->GetSizeBlock())
03611             {
03612               xbuffer.Delete();
03613               ssize = xbuffer.AddWithMask(cmd->GetSOUTMask(), fp->GetSizeBlock()?1:0);
03614 
03615               param  = 1;
03616               status = true;
03617             }
03618 
03619           if(islastblock)
03620             {
03621               XDWORD valuecrc = protocol->CRC32OfFile((*fp->GetTargetName()));
03622               if(crc32!=valuecrc) fp->Erase();
03623 
03624               fp->SetActive(false);
03625             }
03626         }
03627 
03628       delete[] filebuffer;
03629     }
03630 
03631   if(!status) ssize = 0;
03632 
03633   return ssize;
03634 }
03635 
03636 
03637 #ifdef DIO_ALERTS_ACTIVE
03638 
03650 bool DIOPROTOCOL::CMD_SendAlert(DIOALERT* alert)
03651 {
03652   if(!alert) return false;
03653 
03654   DIOPROTOCOL_COMMAND* cmd = GetCommand(DIOPROTOCOL_CMDTYPE_SENDALERT);
03655   if(!cmd) return false;
03656 
03657   XBUFFER  xbuffer;
03658   XSTRING  datetimestr;
03659   XDWORD   applicationversion;
03660   XDWORD   applicationsubversion;
03661   XDWORD   applicationsubversionerr;
03662   bool     result     = false;
03663 
03664   alert->GetDateTime()->GetDateTimeToString(XDATETIME_FORMAT_STANDARD, datetimestr);
03665   alert->GetApplicationVersion(applicationversion, applicationsubversion, applicationsubversionerr);
03666 
03667   xbuffer.AddWithMask(cmd->GetSINMask() , &datetimestr
03668                                         , alert->GetApplicationID()
03669                                         , applicationversion
03670                                         , applicationsubversion
03671                                         , applicationsubversionerr
03672                                         , alert->GetID()                                        
03673                                         , (XBYTE)alert->GetLevel()
03674                                         , alert->GetOrigin()
03675                                         , alert->GetTitle()
03676                                         , alert->Get_Message());
03677 
03678   if(Command(cmd->GetType(), xbuffer))
03679     {
03680       xbuffer.GetWithMask(cmd->GetSOUTMask(), 0, &result);
03681     }
03682 
03683   SendEventCommand(this, DIOPROTOCOL_DIRECTION_SEND, cmd->GetType(), result, NULL, 0);
03684 
03685   return result;
03686 }
03687 
03688 
03704 int DIOPROTOCOL::RCV_SendAlert(DIOPROTOCOL* protocol, DIOPROTOCOL_COMMAND* cmd, XBUFFER& xbuffer, XDWORD rID, XDWORD& param)
03705 {
03706   XSTRING  datetimestr;
03707   XSTRING  applicationID;
03708   XDWORD   applicationversion       = 0;
03709   XDWORD   applicationsubversion    = 0;
03710   XDWORD   applicationsubversionerr = 0;
03711   XDWORD   ID                       = 0;
03712   XDWORD   type                     = 0;
03713   XBYTE    level                    = 0;
03714   XSTRING  origin;
03715   XSTRING  title;
03716   XSTRING  message;
03717   bool     result                   = false;
03718 
03719   datetimestr.AdjustSize(_MAXSTR);
03720   applicationID.AdjustSize(_MAXSTR);
03721   origin.AdjustSize(_MAXSTR);
03722   title.AdjustSize(_MAXSTR);
03723   message.AdjustSize(2048);
03724 
03725   xbuffer.GetWithMask(cmd->GetSINMask(), 0, &datetimestr
03726                                           , &applicationID
03727                                           , &applicationversion
03728                                           , &applicationsubversion
03729                                           , &applicationsubversionerr
03730                                           , &ID
03731                                           , &type
03732                                           , &level
03733                                           , &origin
03734                                           , &title
03735                                           , &message);
03736 
03737   datetimestr.AdjustSize();
03738   applicationID.AdjustSize();
03739   origin.AdjustSize();
03740   title.AdjustSize();
03741   message.AdjustSize();
03742 
03743   if((!title.IsEmpty())  && (!origin.IsEmpty()) && (!message.IsEmpty()))
03744     {
03745       DIOALERT alert;
03746 
03747       alert.GetDateTime()->GetDateTimeFromString(datetimestr, XDATETIME_FORMAT_STANDARD);
03748       alert.GetApplicationID()->Set(applicationID);
03749       alert.SetApplicationVersion(applicationversion, applicationsubversion, applicationsubversionerr);
03750       alert.SetID(ID);      
03751       alert.SetLevel((DIOALERTLEVEL)level);
03752       alert.GetOrigin()->Set(origin);
03753       alert.GetTitle()->Set(title);
03754       alert.Get_Message()->Set(message);
03755 
03756       if(protocol->GetApplicationData())
03757         {
03758           DIOPROTOCOL_APPLICATIONDATA* applicationdata = (DIOPROTOCOL_APPLICATIONDATA*)protocol->GetApplicationData();
03759           if(applicationdata)  result = applicationdata->AddAlert(alert);
03760 
03761           if(result)
03762             {
03763               DIOPROTOCOL_XEVENT xevent(protocol, DIOPROTOCOL_XEVENT_TYPE_ADDALERT);
03764 
03765               xevent.SetProtocol(protocol);
03766               xevent.SetAlert(&alert);
03767 
03768               protocol->PostEvent(&xevent);
03769             }
03770         }
03771     }
03772 
03773   xbuffer.Delete();
03774   return xbuffer.AddWithMask(cmd->GetSOUTMask(), result);
03775 }
03776 
