03780 
03792 void DIOPROTOCOL::ThreadReceived(void* param)
03793 {
03794   DIOPROTOCOL* protocol = (DIOPROTOCOL*)param;
03795   if(!protocol) return;
03796 
03797   if(protocol->doexit) return;
03798 
03799   if(!protocol->xthreadreceived)              return;
03800   if(!protocol->xthreadreceived->IsRunning()) return;
03801 
03802   if(protocol->GetDIOStream())
03803     {
03804       if(protocol->IsConnected())
03805         {
03806           protocol->Received();
03807         }
03808     }
03809 }
03810 
03811 
03823 void DIOPROTOCOL::ThreadAsks(void* param)
03824 {
03825   DIOPROTOCOL* protocol = (DIOPROTOCOL*)param;
03826   if(!protocol) return;
03827 
03828   if(protocol->doexit) return;
03829 
03830   if(!protocol->xthreadreceived)              return;
03831   if(!protocol->xthreadreceived->IsRunning()) return;
03832 
03833   if(protocol->GetDIOStream())
03834     {
03835       if(protocol->IsConnected())
03836         {
03837           if(!protocol->asks.IsEmpty())
03838             {
03839               DIOPROTOCOL_ANSWER* ask = protocol->GetAsk(0);
03840               if(ask)
03841                 {
03842                   XDWORD t = ask->GetType();
03843                   protocol->ReceivedHandle(ask->GetType(), ask->GetID(), ask->GetBuffer());
03844                   protocol->DeleteAsk(0);
03845                 }
03846             }
03847 
03848           //if(protocol->xtimer) protocol->GEN_XSLEEP.MilliSeconds(50);
03849         }
03850     }
03851 }
