03865 {
03866   diostream                     = NULL;
03867 
03868   protocolversion               = DIOPROTOCOL_VERSION;
03869   protocolsubversion            = DIOPROTOCOL_SUBVERSION;
03870   protocolsubversionerr         = DIOPROTOCOL_SUBVERSIONERR;
03871 
03872   applicationversion            = 0;
03873   applicationsubversion         = 0;
03874   applicationsubversionerr      = 0;
03875 
03876   isinitialized                 = false;
03877   isserverinitialized           = false;
03878 
03879   xdatetime                     = NULL;
03880   xtimer                        = NULL;
03881   watchdog                      = NULL;
03882 
03883   xdir                          = NULL;
03884 
03885   connectionsmanager             = NULL;
03886   connection                     = NULL;
03887   applicationdata               = NULL;
03888   isownapplicationdata          = false;
03889 
03890   maskID                        = 0;
03891 
03892   IDmsgcounter                  = 1;
03893   timerfileprogress             = NULL;
03894 
03895   xdir                          = NULL;
03896 
03897   doexit                        = false;
03898 
03899   xmutexanswers                 = NULL;
03900   xmutexasks                    = NULL;
03901   xmutexcommands                = NULL;
03902   xmutexreads                   = NULL;
03903   xmutexwrites                  = NULL;
03904   xmutexfilesprogress           = NULL;
03905 
03906   xthreadreceived               = NULL;
03907   xthreadasks                   = NULL;
03908 }
