01204   while(index < ProtocolConnections_GetNAvailable())
01205     {
01206       DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections.Get(index);
01207       if(protocolconnection)
01208         {
01209           if((!protocolconnection->IsInUse()))
01210             {
01211               DIOPROTOCOL* protocol = protocolconnection->GetDIOProtocol();
01212 
01213               protocolconnections.Delete(protocolconnection);
01214               delete protocolconnection;
01215 
01216               DeleteProtocol(protocol);
01217 
01218             } else index++;
01219 
01220         } else index++;
01221     }
01222 
01223   xmutexprocotolconnections->UnLock();
01224 
01225   return true;
01226 }
