01240 bool DIOPROTOCOL_CONNECTIONSMANAGER::WaitToAnyConnectionIsConnected(int timeout)
01241 {
01242   int  index  = 0;
01243   bool status = true;
01244 
01245   xtimerout->Reset();
01246 
01247   while(1)
01248     {
01249       if(ProtocolConnections_GetNConnected())
01250         {
01251           status = false;
01252           break;
01253         }
01254 
01255       GEN_XSLEEP.MilliSeconds(10);
01256 
01257       if(xtimerout->GetMeasureSeconds() >= (XDWORD)timeout)
01258         {
01259           status = false;
01260           break;
01261         }
01262     }
01263 
01264   return status;
01265 }
