01267 
01279 bool DIOPROTOCOL_CONNECTIONSMANAGER::WaitToAllConnectionsCanBeDeleted(int timeout)
01280 {
01281   if(!xmutexprocotolconnections)  return false;
01282 
01283   xmutexprocotolconnections->Lock();
01284 
01285   int  index  = 0;
01286   bool status = true;
01287 
01288   if(timeout) xtimerout->Reset();
01289 
01290   while(1)
01291     {
01292       bool candelete = true;
01293 
01294       for(int index=0; index< ProtocolConnections_GetNAvailable(); index++)
01295         {
01296           DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections.Get(index);
01297           if(protocolconnection)
01298             {
01299               if(protocolconnection->IsInUse()) candelete = false;
01300             }
01301         }
01302 
01303       if(candelete) break;
01304 
01305       GEN_XSLEEP.MilliSeconds(10);
01306 
01307       if(timeout)
01308         {
01309           if(xtimerout->GetMeasureSeconds() > 0)
01310             {
01311               if(xtimerout->GetMeasureSeconds() >= (XDWORD)timeout)
01312                 {
01313                   status = false;
01314                   break;
01315                 }
01316             }
01317         }
01318     }
01319 
01320   xmutexprocotolconnections->UnLock();
01321 
01322   return status;
01323 }
