01345   xevent.SetDIOStreamConfig(protocolconnection->GetDIOStreamConfig());
01346   xevent.SetDIOProtocol(protocolconnection->GetDIOProtocol());
01347   xevent.SetProtocolConnection(protocolconnection);
01348   xevent.SetProtocolConnectionsManager(this);
