01420 void DIOPROTOCOL_CONNECTIONSMANAGER::ManageProtocolConnectionsServer()
01421 {
01422   if(!xthreadconnections)              return;
01423   if(!xthreadconnections->IsRunning()) return;
01424 
01425   xtimerconnections->Reset();
01426 
01427   ProtocolConnections_DeleteDisconnected();
01428 
01429   if(protocolconnectionsnlimit != DIOPROTOCOL_CONNECTIONS_UNLIMITEDNCONNECTIONS)
01430     {
01431       if(!protocolconnectionsnlimit) return;
01432 
01433       if(ProtocolConnections_GetNConnected() >= protocolconnectionsnlimit)
01434         {
01435           return;
01436         }
01437     }
01438 
01439   ProtocolConnections_SendEventConnected();
01440 
01441   if(!ProtocolConnections_GetNFreeToConnect())
01442     {
01443       DIOPROTOCOL_CONNECTION* protocolconnection = new DIOPROTOCOL_CONNECTION(diostreamcfg);
01444       if(protocolconnection)
01445         {
01446           DIOPROTOCOL* protocol = CreateProtocol();
01447           if(protocol)
01448             {
01449               protocolconnection->SetDIOProtocol(protocol);
01450 
01451               if(protocolconnection->Connect())
01452                 {
01453                   if(xmutexprocotolconnections) xmutexprocotolconnections->Lock();
01454 
01455                   protocol->SetConnection(protocolconnection);
01456 
01457                   protocolconnections.Add(protocolconnection);
01458 
01459                   if(xmutexprocotolconnections) xmutexprocotolconnections->UnLock();
01460                 }
01461             }
01462            else
01463             {
01464               DeleteProtocol(protocol);
01465               protocol = NULL;
01466 
01467               delete protocolconnection;
01468               protocolconnection = NULL;
01469             }
01470         }
01471        else
01472         {
01473           delete protocolconnection;
01474           protocolconnection = NULL;
01475         }
01476     }
01477 }
01478 
