01489 void DIOPROTOCOL_CONNECTIONSMANAGER::ManageProtocolConnectionsClient()
01490 {
01491   if(!xmutexprocotolconnections)       return;
01492   if(!xthreadconnections)              return;
01493   if(!xthreadconnections->IsRunning()) return;
01494   if(!xtimerconnections)               return;
01495   if(!xtimerclienttry)                return;
01496 
01497   ProtocolConnections_DeleteDisconnected();
01498 
01499   if(protocolconnectionsnlimit != DIOPROTOCOL_CONNECTIONS_UNLIMITEDNCONNECTIONS)
01500     {
01501       if(!protocolconnectionsnlimit) return;
01502 
01503       if(ProtocolConnections_GetNConnected() >= protocolconnectionsnlimit)
01504         {
01505           return;
01506         }
01507     }
01508 
01509   if(xtimerclienttry->GetMeasureSeconds() <= DIOPROTOCOL_CONNECTIONS_DEFAULTIMETRYCONNECTIONS) return;
01510 
01511   //DEBUG_PRINT(__L("---------------------------------------------------------------"));
01512 
01513   for(int c=0;c<(int)targetURLs.GetSize();c++)
01514     {
01515       if(!xthreadconnections->IsRunning()) break;
01516 
01517       DIOURL* URLclient = (DIOURL*)targetURLs.Get(c);
01518       if(URLclient)
01519         {
01520           bool haveservervalid = false;
01521 
01522           switch(diostreamcfg->GetType())
01523             {
01524               case DIOSTREAMTYPE_UNKNOWN    : break;
01525               case DIOSTREAMTYPE_UART       : break;
01526               case DIOSTREAMTYPE_USB        : break;
01527               case DIOSTREAMTYPE_BLUETOOTH  : break;
01528 
01529               case DIOSTREAMTYPE_TCPIP      : { DIOSTREAMTCPIPCONFIG* scfg = (DIOSTREAMTCPIPCONFIG*)diostreamcfg;
01530                                                 XSTRING               address;
01531                                                 if(scfg)
01532                                                   {
01533                                                     if(URLclient->IsEmpty() && diostreamenumservers)
01534                                                       {
01535                                                         diostreamenumservers->Search();
01536 
01537                                                         while(diostreamenumservers->IsSearching())
01538                                                           {
01539                                                             GEN_XSLEEP.MilliSeconds(1);
01540                                                           }
01541 
01542                                                         diostreamenumservers->StopSearch(true);
01543 
01544                                                         if(diostreamenumservers->AreDevicesAvailable())
01545                                                           {
01546                                                             DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)diostreamenumservers->GetDevices()->Get(0);
01547                                                             if(device) device->GetIP()->GetXString(address);
01548                                                           }
01549 
01550                                                       } else address = URLclient->Get();
01551 
01552                                                     if(!address.IsEmpty())
01553                                                       {
01554                                                         DIOURL* url= new DIOURL();
01555                                                         if(url)
01556                                                           {
01557                                                             (*url) = address.Get();
01558 
01559                                                             url->ResolveURL();
01560                                                             scfg->GetRemoteURL()->Set(address);
01561                                                             delete url;
01562 
01563                                                             haveservervalid = true;
01564                                                           }
01565                                                       }
01566                                                   }
01567 
01568                                                 break;
01569                                               }
01570 
01571             }
01572 
01573           if(haveservervalid)
01574             {
01575               DIOPROTOCOL_CONNECTION* protocolconnection = new DIOPROTOCOL_CONNECTION(diostreamcfg);
01576               if(protocolconnection)
01577                 {
01578                   DIOPROTOCOL* protocol = CreateProtocol();
01579                   if(protocol)
01580                     {
01581                       protocolconnection->SetDIOProtocol(protocol);
01582 
01583                       if(protocolconnection->Connect())
01584                         {
01585                           xtimerconnections->Reset();
01586 
01587                           DIOSTREAMTCPIPCONFIG* scfg = (DIOSTREAMTCPIPCONFIG*)diostreamcfg;
01588 
01589                           while(1)
01590                             {
01591                               if(protocolconnection->GetDIOProtocol()->IsConnected())
01592                                 {
01593                                   if(xmutexprocotolconnections) xmutexprocotolconnections->Lock();
01594 
01595                                   protocol->SetConnection(protocolconnection);
01596                                   protocolconnections.Add(protocolconnection);
01597 
01598                                   if(xmutexprocotolconnections) xmutexprocotolconnections->UnLock();
01599 
01600                                   SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_CONNECTEDCONNECTION, protocolconnection);
01601 
01602                                   break;
01603                                 }
01604 
01605                               if((xtimerconnections->GetMeasureSeconds()>=5) || (!xthreadconnections->IsRunning()))
01606                                 {
01607                                   DeleteProtocol(protocol);
01608                                   protocol = NULL;
01609 
01610                                   delete protocolconnection;
01611                                   protocolconnection = NULL;
01612 
01613                                   break;
01614                                 }
01615 
01616                               GEN_XSLEEP.MilliSeconds(50);
01617                             }
01618 
01619                          XTRACE_PRINTCOLOR(1, __L("Trying to connect to %s [%s]: %s (%d) seconds."),  URLclient->Get(), scfg->GetRemoteURL()->Get(), protocolconnection?__L("Connected!."):__L("Not Connected!."), xtimerconnections->GetMeasureSeconds());
01620                         }
01621                        else
01622                         {
01623                           DeleteProtocol(protocol);
01624                           protocol = NULL;
01625 
01626                           delete protocolconnection;
01627                           protocolconnection = NULL;
01628                         }
01629                     }
01630                    else
01631                     {
01632                       delete protocolconnection;
01633                       protocolconnection = NULL;
01634                     }
01635                 }
01636             }
01637         }
01638     }
01639 
01640   xtimerclienttry->Reset();
01641 }
