01643 
01655 void DIOPROTOCOL_CONNECTIONSMANAGER::ThreadProtocolConnections(void* param)
01656 {
01657   DIOPROTOCOL_CONNECTIONSMANAGER* protocolconnections = (DIOPROTOCOL_CONNECTIONSMANAGER*)param;
01658   if(!protocolconnections) return;
01659 
01660   if(protocolconnections->isserver)
01661     {
01662       protocolconnections->ManageProtocolConnectionsServer();
01663     }
01664    else
01665     {
01666       protocolconnections->ManageProtocolConnectionsClient();
01667     }
01668 
01669   int c=0;
01670   while(c < protocolconnections->ProtocolConnections_GetNAvailable())
01671     {
01672       DIOPROTOCOL_CONNECTION* protocolconnection = (DIOPROTOCOL_CONNECTION*)protocolconnections->protocolconnections.Get(c);
01673       if(protocolconnection)
01674         {
01675           DIOPROTOCOL* protocol = protocolconnection->GetDIOProtocol();
01676           if(protocol)
01677             {
01678               if(protocol->IsConnected())
01679                 {
01680                   if(!protocol->IsInitialized())
01681                     {
01682                       if(protocolconnections->isserver)
01683                         {
01684                           protocolconnection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01685 
01686                           protocol->GetDIOStream()->GetXTimerNotActivity()->Reset();
01687                           protocolconnections->SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_INITPROTOCOL, protocolconnection);
01688                           if(protocol->IsInitialized()) protocol->CMD_SendIsInitServerProtocol(true);
01689 
01690                           protocolconnection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01691                         }
01692                        else
01693                         {
01694                           XTIMER* GEN_XFACTORY_CREATE(timerout, CreateTimer())
01695                           
01696                           while(1)
01697                             {
01698                               if(protocolconnection->GetDIOProtocol()->IsDisconnected()) break;
01699                               if(protocol->IsServerInitialized())                       break;
01700 
01701                               if(timerout)
01702                                 {
01703                                   if(timerout->GetMeasureSeconds() > 5) break;
01704                                 }
01705 
01706                               GEN_XSLEEP.MilliSeconds(10);
01707                             }
01708 
01709                           GEN_XFACTORY.DeleteTimer(timerout);
01710 
01711 
01712                           if(protocol->IsServerInitialized())
01713                             {
01714                               protocolconnection->SetInUse(true, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01715 
01716                               protocol->GetDIOStream()->GetXTimerNotActivity()->Reset();
01717                               protocolconnections->SendEvent(DIOPROTOCOL_CONNECTIONSMANAGER_XEVENT_TYPE_INITPROTOCOL, protocolconnection);
01718 
01719                               protocolconnection->SetInUse(false, DIOPROTOCOL_CONNECTIONSINUSEID_INIPROTOCOL);
01720                             }
01721                         }
01722 
01723                      XTRACE_PRINTCOLOR((protocol->IsInitialized()?2:4), __L("Protocol Connection Manager: %s Init Protocol %s") , protocolconnections->isserver?__L("Server"):__L("Client"), protocol->IsInitialized()?__L("Ok!"):__L("ERROR!"));
01724 
01725                       if(!protocol->IsInitialized())
01726                         {
01727                           protocolconnections->ProtocolConnections_Disconnect(c);
01728                         }
01729                     }
01730                    else
01731                     {
01732                       if(protocol->GetDIOStream()->GetXTimerNotActivity()->GetMeasureSeconds() > DIOPROTOCOL_CONNECTIONS_DEFAULTIMECHECKCONNECTIONS)
01733                         {
01734                           XDWORD milliseconds;
01735 
01736                           bool status = protocol->CMD_Ping(milliseconds);
01737                           if(!status)
01738                             {
01739                               protocolconnections->ProtocolConnections_Disconnect(c);
01740 
01741                              XTRACE_PRINTCOLOR(status?1:4, __L("Protocol Connection Manager: Check connection protocol %s"), status?__L("Ok!"):__L("ERROR!"));
01742                             }
01743                         }
01744                     }
01745                 }
01746             }
01747         }
01748 
01749       c++;
01750     }
01751 }
