01464         {
01465           DIOSMTPRECIPIENT* recipient = recipients.Get(c);
01466           if(recipient)
01467             {
01468               string.Format(__L("%s<%s>"), recipient->GetName()->Get(), recipient->GetEmail()->Get());
01469 
01470               switch(recipient->GetType())
01471                 {
01472                   case DIOSMTPRECIPIENTTYPE_UNKNOWN : break;
01473 
01474                   case DIOSMTPRECIPIENTTYPE_TO      : if(!to_line.IsEmpty()) to_line+= __L(",");
01475                                                       to_line += string.Get();
01476                                                       break;
01477 
01478                   case DIOSMTPRECIPIENTTYPE_CC      : if(!cc_line.IsEmpty()) cc_line+= __L(",");
01479                                                       cc_line += string.Get();
01480                                                       break;
01481 
01482                   case DIOSMTPRECIPIENTTYPE_BCC     : if(!bcc_line.IsEmpty()) bcc_line+= __L(",");
01483                                                       bcc_line += string.Get();
01484                                                       break;
01485                 }
01486             }
01487         }
01488     }
01489 
01490   string.Empty();
01491 
01492   /*
01493   XDATETIME* GEN_XFACTORY_CREATE(datetime, CreateTime())
01494   if(datetime)
01495     {
01496       datetime->Read();
01497 
01498       string.Format(__L("Date: %d %s %d %d:%d:%d\r\n"), datetime->GetDay()
01499                                                       , month[datetime->GetMonth()-1]
01500                                                       , datetime->GetYear()
01501                                                       , datetime->GetHours()
01502                                                       , datetime->GetMinutes()
01503                                                       , datetime->GetSeconds());
01504 
01505 
01506       GEN_XFACTORY.DeleteTime(datetime);
01507     }
01508 
01509   if(string.IsEmpty()) return false;
01510   header+= string;
01511   */
01512 
01513   string.Format(__L("From: %s <%s>\r\n"),   GetSenderName()->Get(), GetSenderEmail()->Get());
01514   header+= string;
01515 
01516 
01517   if(!GetReplytoEmail()->IsEmpty())
01518     {
01519       string.Format(__L("Reply-To: %s\r\n"),  GetReplytoEmail()->Get());
01520       header+= string;
01521     }
01522 
01523   XSTRING defaultxmailer;
01524 
01525   defaultxmailer  = GEN_VERSION.GetName()->Get();
01526   defaultxmailer += __L(" ");
01527   defaultxmailer += GEN_VERSION.GetVersion()->Get();
01528 
01529   string.Format(__L("X-Mailer: %s\r\n"),  GetXMailer()->IsEmpty()? defaultxmailer.Get() : GetXMailer()->Get());
01530   header+= string;
01531 
01532   switch(xpriority)
01533     {
01534       case DIOSMTPXPRIORITY_HIGH    : string = __L("X-Priority: 2 (High)\r\n");     break;
01535       case DIOSMTPXPRIORITY_UNKNOWN :
01536       case DIOSMTPXPRIORITY_NORMAL  : string = __L("X-Priority: 3 (Normal)\r\n");   break;
01537       case DIOSMTPXPRIORITY_LOW     : string = __L("X-Priority: 4 (Low)\r\n");      break;
01538     }
01539 
01540   if(string.IsEmpty()) return false;
01541   header+= string;
01542 
01543   if(!to_line.IsEmpty())
01544     {
01545       string.Format(__L("To: %s\r\n"),  to_line.Get());
01546       header+= string;
01547     }
01548 
01549   if(!cc_line.IsEmpty())
01550     {
01551       string.Format(__L("Cc: %s\r\n"),  cc_line.Get());
01552       header+= string;
01553     }
01554 
01555   if(!bcc_line.IsEmpty())
01556     {
01557       string.Format(__L("Bcc: %s\r\n"), bcc_line.Get());
01558       header+= string;
01559     }
01560 
01561 
01562   string.Empty();
01563   switch(contenttype)
01564     {
01565       case DIOSSMPTCONTENTTYPE_PLAINTTEXT : string.Format(__L("Subject: %s\r\n"), GetSubject()->IsEmpty()?__L(" "):GetSubject()->Get());
01566                                             header+= string;
01567                                             break;
01568 
01569       case DIOSSMPTCONTENTTYPE_UTF8       : { XBUFFER subjectUTF8;
01570                                               XSTRING subjectbase64;
01571 
01572                                               GetSubject()->ConvertToUTF8(subjectUTF8);
01573 
01574                                               subjectUTF8.ConvertToBase64(subjectbase64);
01575 
01576                                               string = __L("Subject: =?UTF-8?B?");
01577                                               string += subjectbase64;
01578                                               string += __L("?=\r\n");
01579                                             }
01580                                             break;
01581     }
01582 
01583   header += string;
01584 
01585 
01586   string = __L("MIME-Version: 1.0\r\n");
01587 
01588   if(attachments.GetSize())
01589     {
01590       XSTRING string2;
01591       string2.Format(__L("Content-Type: multipart/mixed; boundary=\"%s\"\r\n\r\n--%s\n\r"), DIOSMTP_BOUNDARYTEXT, DIOSMTP_BOUNDARYTEXT);
01592       string += string2;
01593     }
01594 
01595   switch(contenttype)
01596     {
01597       case DIOSSMPTCONTENTTYPE_PLAINTTEXT : string += __L("Content-type: text/plain; charset=utf-8\r\n"); break;
01598       case DIOSSMPTCONTENTTYPE_UTF8       : string += __L("Content-type: text/plain; charset=utf-8\r\n"); break;
01599     }
01600 
01601 //string += __L("Content-Transfer-Encoding: 7bit\r\n");
01602   string += __L("\r\n");
01603 
01604   header += string;
01605 
01606   return true;
01607 }
