00105 bool DIOSNMP_XBER::SetIPADDRESS(XCHAR* IPstring)
00106 {
00107   if(!IPstring)     return false;
00108   if(!IPstring[0])  return false;
00109   if(!data)         return false;
00110 
00111   data->Delete();
00112 
00113   XSTRING       string;
00114   XVECTOR<int>  numbers;
00115   int           start = 0;
00116   int           end   = 0;
00117 
00118   string = IPstring;
00119 
00120   while(1)
00121     {
00122       XSTRING numberstring;
00123       int     indexstring = string.Find(__L("."),true,start);
00124 
00125       if(indexstring != XSTRING_NOTFOUND)
00126         {
00127           end = indexstring;
00128           string.Copy(start,end,numberstring);
00129           numbers.Add(numberstring.ConvertToInt());
00130           start = end+1;
00131         }
00132        else
00133         {
00134           string.Copy(end+1,numberstring);
00135           numbers.Add(numberstring.ConvertToInt());
00136 
00137           break;
00138         }
00139     }
00140 
00141   if(!numbers.GetSize()) return false;
00142 
00143   for(XDWORD c=0;c<numbers.GetSize();c++)
00144     {
00145       size_t _number = (size_t)(numbers.Get(c));
00146       int number = (int)(_number);
00147       data->Add((XBYTE)(number));
00148     }
00149 
00150   this->type = (XBYTE)(DIOSNMPBERTYPE_IPADDRESS | XBERTYPE_ISAPPLICATION);
00151   this->size = data->GetSize();
00152 
00153   return true;
00154 }
