00344 bool DIOSNMP_BASE::Open(XCHAR* IPtarget, int port, bool checkavailableNMS, XCHAR* localIP)
00345 {
00346   if(!IPtarget)     return false;
00347   if(!IPtarget[0])  return false;
00348 
00349   diostreamudpcfg = new DIOSTREAMUDPCONFIG();
00350   if(!diostreamudpcfg)  return false;
00351 
00352   diostreamudpcfg->SetMode(DIOSTREAMMODE_CLIENT);
00353 
00354   if(localIP) diostreamudpcfg->GetLocalIP()->Set(localIP);
00355 
00356   diostreamudpcfg->GetRemoteURL()->Set(IPtarget);
00357   diostreamudpcfg->SetRemotePort(port);
00358 
00359   bool status = false;
00360 
00361   diostreamudp = (DIOSTREAMUDP*)GEN_DIOFACTORY.CreateStreamIO(diostreamudpcfg);
00362   if(diostreamudp)
00363     {
00364       if(diostreamudp->Open())
00365         {
00366           if(checkavailableNMS)
00367             {
00368               //--------------------------------------------
00369               // Write something to received a ICMP result.
00370               // and check if something is the other side.
00371 
00372               status = diostreamudp->WaitToConnected(3);
00373               if(status)
00374                 {
00375                   XBYTE data[] = { 0xAA,0x55 };
00376                   diostreamudp ->Write(data,sizeof(data));
00377 
00378                   status = diostreamudp->WaitToDisconnected(3);
00379                 }
00380 
00381             } else status = true;
00382         }
00383     }
00384 
00385   if(!status) Close();
00386 
00387   return status;
00388 }
