00671 bool DIOSNMP::Get(DIOSNMP_OPERATION operation, XCHAR* community, XCHAR* OIDstr, DIOSNMP_XBER& xberout)
00672 {
00673   #define MAX_READBUFFER  2048
00674 
00675   if(!diostreamudpcfg)  return false;
00676   if(!diostreamudp)     return false;
00677   
00678   XBUFFER       xbuffer;
00679   XBUFFER       xbufferdata;  
00680   XDWORD        number = 0;
00681   DIOSNMP_XBER  xber;
00682   DIOSNMP_XBER  value;
00683   DIOSNMP_XBER  bindings;
00684   DIOSNMP_XBER  request;
00685   DIOSNMP_XBER  data_next_request;
00686   DIOSNMP_XBER  header;  
00687 
00688   // ----------------------------------------------------------------
00689   // Get-Next 
00690 
00691   xber.SetOID(OIDstr);                                        value.Sequence_AddTo(xber);          // OID
00692   xber.SetNULL();                                             value.Sequence_AddTo(xber);          // NULL  
00693 
00694   XRAND* GEN_XFACTORY_CREATE(xrand, CreateRand())
00695   if(xrand)
00696     {
00697       number = xrand->Max(65000);
00698       GEN_XFACTORY.DeleteRand(xrand);
00699     }
00700 
00701   xber.SetINTEGER(number);                                    bindings.Sequence_AddTo(xber);        // Request-ID 
00702   xber.SetINTEGER(0);                                         bindings.Sequence_AddTo(xber);        // Error-status 
00703   xber.SetINTEGER(0);                                         bindings.Sequence_AddTo(xber);        // Error-index
00704 
00705   request.Sequence_AddTo(value);
00706   bindings.Sequence_AddTo(request);  
00707   
00708   data_next_request.Sequence_AddCommandTo(DIOSNMP_OPERATION_GETNEXTREQUEST, bindings); 
00709 
00710   xber.SetINTEGER(version);                                   header.Sequence_AddTo(xber);        // Version 
00711   xber.SetOCTETSTRING(community);                             header.Sequence_AddTo(xber);        // Community   
00712   
00713   header.Sequence_AddTo(data_next_request);     
00714 
00715   header.GetDump(xbuffer);
00716 
00717   XDWORD bw = diostreamudp->Write(xbuffer.Get(), xbuffer.GetSize());
00718   if(bw!= xbuffer.GetSize()) return false;
00719 
00720   xbuffer.Empty();
00721   xbuffer.Resize(MAX_READBUFFER);
00722 
00723   XTIMER* GEN_XFACTORY_CREATE(timeout, CreateTimer())
00724   if(!timeout) return false;
00725 
00726   do{ XDWORD sizeread  = diostreamudp->GetInXBuffer()->GetSize();
00727       if(sizeread)
00728         {
00729           XDWORD br = diostreamudp->Read(xbuffer.Get(), sizeread);
00730           if(br)
00731             {
00732               timeout->Reset();
00733               xbufferdata.Add(xbuffer.Get(), br);  
00734             }      
00735         }
00736       
00737       if(timeout->GetMeasureSeconds() > 2) break; 
00738 
00739     } while(1);
00740 
00741   GEN_XFACTORY.DeleteTimer(timeout);
00742 
00743   if(xbufferdata.GetSize())
00744     {
00745       xberout.SetType(xbufferdata.Get()[0]);
00746       xberout.SetSize(xbufferdata.Get()[1]);  
00747       xberout.GetData()->Add(&xbufferdata.Get()[2], xbufferdata.GetSize());
00748     }
00749   
00750   return xbufferdata.GetSize()?true:false;
00751 }
