00472 bool DIOSPILCDDISPLAYPCF8833::SetWindow(XWORD x0, XWORD y0, XWORD x1, XWORD y1)
00473 {
00474   XBYTE   paset = 0;
00475   XBYTE   caset = 0;
00476   XBYTE   ramwr = 0;
00477 
00478   switch(type)
00479     {
00480       case DIOSPILCDDISPLAYPCF8833_EPSON     :  paset = DIOSPILCDDISPLAYPCF8833_EPSON_CMD_PASET;
00481                                                 caset = DIOSPILCDDISPLAYPCF8833_EPSON_CMD_CASET;
00482                                                 ramwr = DIOSPILCDDISPLAYPCF8833_EPSON_CMD_RAMWR;
00483                                                 break;
00484 
00485       case DIOSPILCDDISPLAYPCF8833_PHILLIPS  :  paset = DIOSPILCDDISPLAYPCF8833_PHILLIPS_CMD_PASET;
00486                                                 caset = DIOSPILCDDISPLAYPCF8833_PHILLIPS_CMD_CASET;
00487                                                 ramwr = DIOSPILCDDISPLAYPCF8833_PHILLIPS_CMD_RAMWR;
00488                                                 break;
00489 
00490                                    default   :  return false;
00491     }
00492 
00493   XBUFFER databuffer;
00494 
00495   Buffer_Delete(databuffer);
00496 
00497   // columns
00498   DIOSPILCDDISPLAYPCF8833_ADDCOMMAND(databuffer, caset);
00499   DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, x0);
00500   DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, x1);
00501 
00502   // rows
00503   DIOSPILCDDISPLAYPCF8833_ADDCOMMAND(databuffer, paset);
00504   DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, y0);
00505   DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, y1);
00506 
00507   // start write
00508   DIOSPILCDDISPLAYPCF8833_ADDCOMMAND(databuffer, ramwr);
00509 
00510   return Buffer_Send(databuffer);
00511 }
