00528 bool DIOSPILCDDISPLAYPCF8833::Draw_HLine(XBYTE x, XBYTE y, XBYTE width, XWORD color)
00529 {
00530   if((width < 1) || (width > DIOSPILCDDISPLAYPCF8833_ROW_LENGTH)) return false;
00531 
00532   SetWindow(x, y, (x + width - 1), y);
00533 
00534   XBUFFER databuffer;
00535 
00536   Buffer_Delete(databuffer);
00537 
00538   for(XBYTE i=0; i<width; )
00539     {
00540       i += 2;
00541 
00542       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, ((color & 0xFF0) >> 0x04));                                // R1G1
00543       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, ((color & 0x00F) << 0x04) | ((color & 0xF00) >> 0x08));    // B1R2
00544       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer,  (color & 0x0FF));                                         // G2B2
00545     }
00546 
00547   return Buffer_Send(databuffer);
00548 }
