00570 
00571   XBUFFER databuffer;
00572 
00573   Buffer_Delete(databuffer);
00574 
00575   for(XBYTE i=0; i<width; )
00576     {
00577       XWORD c1 = *buffer++;
00578       XWORD c2 = *buffer++;
00579 
00580       i += 2;
00581 
00582       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer,  (c1 & 0xFF0) >> 0x04);                              // R1G1
00583       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer, ((c1 & 0x00F) << 0x04) | ((c2 & 0xF00) >> 0x08));    // B1R2
00584       DIOSPILCDDISPLAYPCF8833_ADDDATA(databuffer,  (c2 & 0x0FF));                                      // G2B2
00585     }
