00164 bool DIOSPIOLEDDISPLAYSSD1306::Clear(XBYTE color)
00165 {
00166   XBYTE nblocks;
00167 
00168   nblocks = (width >> 4) * (height >> 3);
00169 
00170   // Set columns
00171   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_COLUMN_ADDRESS_SET);     // 0x21
00172   SendCommand(0x00);                                                    // Start
00173   SendCommand(width - 1);                                               // End
00174 
00175   //Set rows
00176   SendCommand(DIOSPIOLEDDISPLAYSSD1306_COMMAND_PAGE_ADDRESS_SET);       // 0x22
00177   SendCommand(0x00);                                                    // Start
00178   SendCommand(height - 1);                                              // End
00179 
00180   XBYTE buffer[16];
00181 
00182   memset(buffer, color, 16);
00183 
00184   //Send buffer
00185   for(XBYTE c=0; c<nblocks; c++)
00186     {
00187       SendData(buffer, 16);
00188     }
00189 
00190   return true;
00191 }
