00363 bool DIOSPIOLEDDISPLAYSSD1306::Reset()
00364 {
00365   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET) == DIOGPIO_ID_NOTDEFINED)   return false;
00366   if(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC)    == DIOGPIO_ID_NOTDEFINED)   return false;
00367             
00368   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_DC), false);
00369   GEN_XSLEEP.MilliSeconds(10); 
00370   
00371   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), false);
00372   GEN_XSLEEP.MilliSeconds(10);
00373 
00374   GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET), true);
00375   GEN_XSLEEP.MilliSeconds(10);
00376 
00377   return true;
00378 }
